INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (851, NULL, 'core_histories', 'coh_peripheral_art_dis_yn', '<p>Indicate if the patient has a history of peripheral arterial disease (includes upper and lower extremity, renal, mesenteric, and abdominal aortic systems).</p>', '<p>Any occurrence between birth and arrival at this facility</p>', '<p>:<strong> Peripheral Arterial Disease:</strong></p>
<ul>
<li>&nbsp; Current or previous history of peripheral arterial disease (includes subclavian, iliac, femoral, and upper- and lower-extremity&nbsp;&nbsp; vessels; excludes renal, coronary, cerebral, and mesenteric vessels and aneurysms). This can include:&nbsp;</li>
<li>&nbsp;Claudication on exertion&nbsp;</li>
<li>&nbsp;Amputation for arterial vascular insufficiency&nbsp;</li>
<li>Vascular reconstruction, bypass surgery, or percutaneous revascularization in the arteries of the extremities&nbsp;</li>
<li>Positive noninvasive test (e.g., ankle brachial index &lt;= 0.9, ultrasound, MR or CT imaging of &gt;50% diameter stenosis in any&nbsp;&nbsp;peripheral artery (i.e., subclavian, femoral, iliac) or angiographic imaging)</li>
<li>Source: ACCF/AHA 2011 Key Data Elements and Definitions of a Base Cardiovascular Vocabulary for Electronic Health&nbsp;&nbsp;Records (JACC 2011;58;202-222)</li>
</ul>
<p>&nbsp;</p>', '<p>Does not include venous conditions (e.g. DVT).</p>', 'ACTION Registry v2.4', NULL, 'Peripheral Arterial Disease', NULL, NULL, NULL, NULL, NULL, 732, '2016-09-14 15:57:00', '2018-06-06 14:23:27', NULL);
