INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2100, NULL, 'tavr_devices', 'tavr_dev_success_yn', '<p>Indicate if the device deployment was successful, as defined by the Standardized Endpoint&nbsp;</p>', NULL, '<p><strong>Transcatheter Valve Device Success:&nbsp;</strong></p>
<p><em>Device Success is defined as all of the following:&nbsp;</em></p>
<ul>
<li>1. Successful vascular access, delivery and deployment of the device and successful retrieval of the delivery system,</li>
<li>2. Correct position of the device in the proper anatomical location,&nbsp;</li>
<li>3. Intended performance of the prosthetic heart valve (aortic valve area &gt;1.2 cm2 and mean aortic valve gradient &lt;20 mm Hg or&nbsp;peak velocity &lt;3 m/s, without moderate or severe prosthetic valve AR),</li>
<li>4. Only one valve implanted in the proper anatomical location.</li>
</ul>
<p>Source: Standardized Endpoint Definitions for Transcatheter Aortic Valve Implantation Clinical Trials (JACC, 2011, vol 57, No&nbsp; &nbsp; &nbsp;3)</p>
<p>&nbsp;</p>', '<p>Definitions for Transcatheter Aortic Valve Implantation Clinical Trials (JACC, 2011, vol 57, No 3).</p>', 'TVT Registry v2.0', NULL, 'Device Success', NULL, NULL, NULL, NULL, NULL, 780, '2016-09-14 15:57:00', '2018-08-06 12:51:23', NULL);
