INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (341, NULL, 'electrocardiograms', 'ecg_st_elevation_yn', '<p>Indicate if there is ST Elevation</p>', NULL, '<ul>
<li>1 For men 40 years of age and older, the threshold value for abnormal J-point elevation should be 0.2 mV (2 mm) in leads V2 and V3 and 0.1 mV (1 mm) in all other leads.</li>
<li>2 For men less than 40 years of age, the threshold values for abnormal J-point elevation in leads V2 and V3 should be 0.25 mV (2.5 mm).</li>
<li>3 For women, the threshold value for abnormal J-point elevation should be 0.15 mV (1.5 mm) in leads V2 and V3 and greater than 0.1 mV (1 mm) in all other leads.</li>
<li>4 For men and women, the threshold for abnormal J-point elevation in V3R and V4R should be 0.05 mV (0.5 mm), except for males less than 30 years of age, for whom 0.1 mV (1 mm) is more appropriate.</li>
<li>5 For men and women, the threshold value for abnormal J- point elevation in V7 through V9 should be 0.05 mV (0.5 mm).</li>
<li>6 For men and women of all ages, the threshold value for abnormal J-point depression should be &minus;0.05 mV (&minus;0.5 mm) in leads V2 and V3 and &minus;0.1 mV (&minus;1 mm) in all other leads.</li>
</ul>', NULL, 'J Am Coll Cardiol. 2009;53(11):1003-1011. doi:10.1016/j.jacc.2008.12.016', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 738, '2016-09-14 15:57:00', '2018-06-07 14:40:41', NULL);
