INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1343, NULL, 'tavr_histories', 'tavr_hostile_chest_yn', '<p>Indicate if the patient has a medical condition that precludes an open chest procedure and that is documented in the medical record.</p>', '<p>The value on the procedure</p>', '<ul>
<li>&nbsp;This can include any of the following or other reasons that make redo operation through sternotomy or right anterior&nbsp;&nbsp;thoracotomy prohibitively hazardous:&nbsp;
<ul>
<li>&nbsp;1. Evidence of abnormal chest wall anatomy due to severe kyphoscoliosis or other skeletal abnormalities (including thoracoplasty,&nbsp;&nbsp;Potts&nbsp;disease, sternal bone destruction, evidence of indetectable plane between posterior sternal table and important mediastinal&nbsp;&nbsp;structures )</li>
<li>&nbsp;2. Complications from prior surgery&nbsp;</li>
<li>&nbsp;3. Prior radiation involving the mediastinum/thoracic, or evidence of severe radiation damage (e.g., skin burns, bone destruction,&nbsp;muscle loss, lung fibrosis or esophageal stricture)&nbsp;</li>
<li>&nbsp;4. History of multiple recurrent pleural effusions causing internal adhesions.&nbsp;</li>
<li>&nbsp;5. Chronic, ongoing open skin defects or extremely severe soft tissue atrophy.&nbsp;</li>
<li>&nbsp;6. Complete absence of reconstructive options based on plastic surgeon consult.</li>
</ul>
</li>
</ul>
<p>&nbsp;</p>', '<p>No Yes</p>', 'TVT Registry v2.0', NULL, 'Hostile Chest', NULL, NULL, NULL, NULL, NULL, 786, '2016-09-14 15:57:00', '2018-08-06 06:39:36', NULL);
