INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1204, NULL, 'functional_states', 'funs_cognition_level', '<p>Indicate the patients level of cognition.</p>', '<p>The value on arrival at this facility</p>', NULL, '<p><strong>Cognition is scored on three levels of neuropsychiatric functioning:</strong></p>
<ul>
<li>Normal, Mildly impaired due to dementia or depression, and Moderate/severely impaired due to dementia or depression.</li>
<li>Mildly impaired cognitive function includes mild dementia, not limiting simple exchanges, and mild depression.
<ul>
<li>Such mild impairments may be only identified by family or may be a comorbid diagnosis treated with medications yet requiring minimal support. Short term memory loss is often present from the beginning of this spectrum.</li>
</ul>
</li>
<li>Moderate or severely impaired cognitive impairment includes notable short term memory loss, disorientation, and/or confusion which limits the ability to participate in simple exchanges.
<ul>
<li>Moderate to severe cognitive impairment may also result from severe depression which has many of the same manifestations and consequences.Normal Mildly Impaired Mod/Severly impaired Unknown</li>
</ul>
</li>
</ul>', 'Action Registry', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 899, '2016-09-14 15:57:00', '2018-06-20 15:43:30', NULL);
