INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1104, NULL, 'universal', 'uni_duke_score', '<p>Indicate the risk score of the standard exercise stress test</p>', NULL, '<p><strong>The risk score is derived by the Duke Treadmill score</strong>&nbsp;</p>
<ul>
<li>Which is an exercise treadmill score that predicts prognosis in coronary artery disease.</li>
<li>It is calculated as follows:
<ul>
<li>Treadmill score = exercise time - (5 x ST-segment deviation in millimeters*) - (4 x treadmill angina index**) * ST-segment deviation can be measured at 60 to 80 ms after the J point.</li>
<li>If the amount of exercise-induced ST-segment deviation is less than 1 mm, than value entered into the score for ST deviation is 0.</li>
<li>** The treadmill anginal index has a value of 0 if there was no exercise angina, 1 if exercise angina occurred, and 2 if angina was the reason the patient stopped exercising.</li>
<li>Exercise time is based on a standard Bruce protocol.</li>
<li>The Duke Treadmill Score was published by Daniel, Mark, et. al, in the Annals of Internal Medicine, June 1987, Vol 106, 6​</li>
</ul>
</li>
</ul>', '<p>DTS = &nbsp; &nbsp; Exercise Time - (5 x Max ST) - (4 x Angina Index)</p>
<p>&nbsp;</p>
<ul>
<li>Ex Time&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Treadmill exercise time</li>
<li>Max ST&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Maximum net ST deviation (except aVR)</li>
<li>Angina Index&nbsp;&nbsp; &nbsp; Treadmill angina index
<ul>
<li>0.&nbsp;&nbsp; &nbsp;No angina during exercise</li>
<li>1.&nbsp;&nbsp; &nbsp;Non-limiting angina</li>
<li>2.&nbsp;&nbsp; &nbsp;Exercise limited angina</li>
</ul>
</li>
<li>DTS&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Duke treadmill score
<ul>
<li>&nbsp; &nbsp;&gt;=+5&nbsp;&nbsp; &nbsp;Low risk</li>
<li>&nbsp; &nbsp;+4 to -10&nbsp;Moderate risk</li>
<li>&nbsp; &nbsp;&lt;= -11&nbsp;&nbsp; &nbsp;High risk</li>
</ul>
</li>
</ul>', 'Canadian ACS Definitions', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 918, '2016-09-14 15:57:00', '2018-07-04 14:33:37', NULL);
