INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (458, NULL, 'acs_first_contacts', 'fc_arrest_first_contact_yn', '<p>Indicate if the patient was in cardiac arrest when first evaluated by EMS or ED personnel.</p>', '<p>Any occurrence between first medical contact and arrival at this facility</p>', '<p><strong>Cardiac Arrest:</strong></p>
<ul>
<li>''Sudden'' cardiac arrest is the sudden cessation of cardiac activity so that the victim becomes unresponsive, with no normal breathing and no signs of circulation.</li>
<li>If corrective measures are not taken rapidly, this condition progresses to sudden death.&nbsp;</li>
<li>Cardiac arrest should be used to signify an event as described above that is reversed, usually by CPR, and/or defibrillation or cardioversion, or cardiac pacing</li>
<li>Sudden cardiac arrest is not the same as sudden cardiac death. Sudden cardiac death describes a fatal event.</li>
</ul>
<p><strong>Source:</strong> ACC/AHA/HRS 2006 Key Data Elements and Definitions for Electrophysiological Studies and Procedures</p>', '<p><strong>Note(s)</strong>:</p>
<p>Evaluated by EMS or ED personnel and either</p>
<p>(1) received attempts at external defibrillation (by lay responders or emergency personnel) or chest compressions by organized EMS or ED personnel or</p>
<p>(2) were pulseless but did not receive attempts to defibrillate or cardiopulmonary resuscitation (CPR) by EMS personnel. If the patient experienced a cardiac arrest after arrival at this facility, code the event as an ''In-Hospital Clinical Event''. See Section H.</p>', 'ACTION Registry v2.4', NULL, 'Cardiac Arrest at First Medical Contact', NULL, NULL, NULL, NULL, NULL, 816, '2016-09-14 15:57:00', '2018-06-23 09:00:50', NULL);
