INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2148, NULL, 'chd_risk_factors', 'chd_renal_insufficiency_yn', '<p>Indicate if the patient has a history of renal insufficiency.</p>', '<p>Any occurrence between birth and discharge</p>', '<p><strong>Renal Insufficiency:</strong></p>
<ul>
<li>Patient has reduced glomerular filtration rate (GFR) for at least 3 months.</li>
<li>Degree of renal insufficiency may be further defined&nbsp;according to degree of depression in GFR:
<ul>
<li>Mild renal insufficiency: GFR 60 to 89 ml/min/1.73 m2.</li>
<li>Moderate renal insufficiency: GFR 30 to 59 ml/min/1.73 m2.</li>
<li>Severe renal insufficiency: GFR 15 to 29 ml/min/1.73 m2.&nbsp;</li>
<li>Renal failure: GFR 15 ml/min/1.73 m2, or patient requires chronic dialysis treatment.</li>
</ul>
</li>
</ul>
<p><span style="background-color: #f0fff0;">Source: ACC-AHA Clinical Data Standards</span></p>
