INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2101, NULL, 'tavr_devices', 'tavr_valve_dev_udi', '<p>[Reserved for Future Use] Indicate the direct identifier portion of the Unique Device Identifier (UDI) associated with the device used during the procedure. This ID is provided by the device manufacturer, and is either a GTIN or HIBBC number.</p>', NULL, '<p>An identifier that is the main (primary) lookup for a medical device product and meets the requirements to uniquely identify a<br />device through its distribution and use. This value is supplied to the FDA by the manufacturer.<br />Http://www.fda.gov/MedicalDevices/DeviceRegulationandGuidance/UniqueDeviceIdentification/default.htm<br />Source: FDA.gov</p>
<p>&nbsp;</p>', '<p>The direct identifier portion of the UDI (unique device identifier) is provided by the manufacturer, specified at the unit of use. This is<br />not the package barcode. The value should be mapped from your supply chain or inventory management system into this field.<br />Depending on the device and manufacturer, this number could be between 12 to 25 digits.</p>
<p>- GTIN / GS1 standard: numeric, 12 &acirc;&euro;&ldquo; 14 characters<br />- HIBCC standard: alphanumeric, 25 characters<br />- ISBT-128: alphanumeric, 25 characters</p>
<p>If a device was not used, leave blank.</p>
<p>&nbsp;</p>
<p>&nbsp;</p>', 'TVT Registry v2.0', NULL, 'Valve Device UDI Direct Identifier', NULL, NULL, NULL, NULL, NULL, 780, '2016-09-14 15:57:00', '2018-08-05 09:23:33', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1712, NULL, 'blood_samples', 'bs_cholesterol_total_val', '<p>Indicate the total cholesterol value in mg/dL.</p>', '<p>The last value between 6 months before first medical contact and discharge</p>', NULL, '<ul>
<li>Lipids obtained within the first 24 hours of arrival at this facility should take precedence.</li>
<li>If greater than 24 hours after arrival, then enter the most recent values obtained (within six months) prior to arrival at this facility.</li>
<li>If an exact value could not be determined by the lab because it is too high or low (e.g., &gt; 300 mg/dL, &lt; 20 mg/dL, or no value wasreported), do not indicate a value for this element and indicate "Yes" for Lipid Panel Value Out of Range.</li>
</ul>
<p>&nbsp;</p>', 'ACTION Registry v2.4', NULL, 'Total Cholesterol', NULL, NULL, NULL, NULL, NULL, 697, '2016-09-14 15:57:00', '2018-10-31 08:21:15', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1732, NULL, 'core_histories', 'coh_cerebrovasc_dis_yn', '<p>Indicate if the patient has a history of cerebrovascular disease.</p>', '<p>Any occurrence between birth and arrival at this facility</p>', '<p><strong>Cerebrovascular Disease:&nbsp;</strong>Current or previous history of any of the following:</p>
<ul>
<li>&nbsp;Ischemic stroke: infarction of central nervous system tissue whether symptomatic or silent (asymptomatic).&nbsp;</li>
<li>&nbsp;TIA: transient episode of neurological dysfunction caused by focal brain, spinal cord, or retinal ischemia without acute infarction.&nbsp;</li>
<li>The symptoms typically last less than 24 hours.&nbsp;</li>
<li>Noninvasive or invasive arterial imaging test demonstrating 50% stenosis of any of the major extracranial or intracranial vessels&nbsp;to the brain.&nbsp;</li>
<li>* Previous cervical or cerebral artery revascularization surgery or percutaneous intervention.&nbsp;</li>
<li>This does not include chronic (nonvascular) neurological diseases or other acute neurological insults such as metabolic and&nbsp;anoxic ischemic encephalopathy.</li>
<li>Source: ACCF/AHA 2011 Key Data Elements and Definitions of a Base Cardiovascular Vocabulary for Electronic Health&nbsp;&nbsp; Records (JACC 2011;58;202-222)</li>
</ul>
<p>&nbsp;</p>', NULL, 'ACTION Registry v2.4', NULL, 'Cerebrovascular Disease', NULL, NULL, NULL, NULL, NULL, 732, '2016-09-14 15:57:00', '2018-06-06 03:30:03', NULL);
