INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3542, NULL, 'universal', 'uni_lesion_type_aha', '<p>Indicate The Characteristics of ACC / AHA Type A, B and C lesions</p>', NULL, NULL, '<p>After extensive statistical study of the Krone classification with respect to that model''s ability to predict outcomes following percutaneous coronary interventions (PCI), it was determined that a somewhat simpler model could yield equally good predictive results. This lead to an other classificationn scheme namely the SCAI</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 918, '2017-12-21 05:57:20', '2019-10-08 13:07:43', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1543, NULL, 'kccq12_questions', 'kccq12_score', '<p>(Auto Calculated) This field is auto-populated by your application.</p>
', NULL, '<p><strong>Scores 0 - 100 [Higher = Better]</strong></p>

<p>In studies as &nbsp;Am Heart J 2005; 150: 707-15 the overall score was</p>

<ul>
	<li><strong>In NYHA IV the score is 0 - 45</strong></li>
