INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (379, NULL, 'cath_stresses', 'standard_exercise_test_risk', 'Indicate the risk score of the standard exercise stress test.', 'The last value between 6 months prior to procedure and current procedure', '<p>The risk score is&nbsp;derived by the Duke Treadmill score which is an exercise treadmill score that&nbsp;predicts prognosis in coronary artery disease. It is calculated as follows:</p>

<p>Treadmill score = exercise time - (5 x ST-segment deviation in&nbsp;millimeters*) - (4 x treadmill angina index**)</p>

<p>* ST-segment deviation can be measured at 60 to 80 ms after the J point. If&nbsp;the amount of exercise-induced ST-segment deviation is less than 1 mm, the&nbsp;value entered into the score for ST deviation is 0.<br />
** The treadmill anginal index has a value of 0 if there was no exercise&nbsp;angina, 1 if exercise angina occurred, and 2 if angina was the reason the&nbsp;patient stopped exercising.<br />
Exercise time is based on a standard Bruce protocol.&nbsp;</p>

<p>The Duke Treadmill Score was published by Daniel, Mark, et. al, in the&nbsp;Annals of Internal Medicine, June 1987, Vol 106, #6</p>

<p>&nbsp;</p>
', '', 'CathPci Registry v4.4', NULL, 'Risk/Extent of Ischemia (Stress Test)', NULL, NULL, NULL, NULL, NULL, 709, '2016-09-14 15:57:00', '2016-09-14 15:56:26', NULL);
