INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2201, NULL, 'asd_closures', 'asdcl_stretched_diam_size', '<p>Indicate the atrial septal defect (ASD) stretched diameter size in millimeters.</p>', '<p>The value on current procedure</p>', NULL, '<p><strong>An example:&nbsp;Method&nbsp;of Measurment</strong></p>
<ul>
<li>During catheterization, an appropriate occluder device size was estimated by Amplatzer stretched balloon sizing (AGA Medical).</li>
<li>A cylindrical sizing balloon was inflated in the left atrium and then pulled back against the ASD.</li>
<li>It was thereafter deflated to reach a size sufficient to enable it to be pulled into the right atrium through the defect.</li>
<li>BOD was defined as the balloon size that completely occluded the ASD and prevented any shunt across the defect without deforming the balloon.</li>
<li>The balloon diameter was measured directly on the screen connected to the fluoroscopy.</li>
<li>TEE was used to ensure that the balloon was perpendicular to the septum during the balloon sizing of the ASD.</li>
<li>After releasing the device from the cable by unscrewing it, a final TEE examination was performed to demonstrate the position of the device and any residual shunting.</li>
<li>J Tehran Heart Cent. 2010 Spring; 5(2): 74&ndash;77.&nbsp;Published online 2010 May 31.Source:&nbsp;</li>
</ul>', 'IMPACT Registry v1.0.1', NULL, 'ASD - Stretched Diameter Size', NULL, NULL, NULL, NULL, NULL, 695, '2016-09-14 15:57:00', '2018-06-26 15:19:24', NULL);
