INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (49, NULL, 'cor_anatomies', 'cor_anat_lad_d1_type', 'First Diagonal variations - Types', NULL, 'There are termed "diagonal" due to them branching from their parent vessel at acute angles. They extend over the left ventricle in a diagonal fashion toward the acute margin and the apex. They often run parallel to one another and are variable in number (often 2 to 9). If a ramus intermedius artery is present, the diagonal arteries are less prominent and arise more distally. The first diagonal (D1) branch tends to be the most prominent. When the first diagonal is large, the other diagonal vessels tend to be small and run a shorter course.', 'Most diagonal branches follow a predictable oblique pathway reflective of the orientation of the underlying superficial myocardial fibers', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 726, '2019-07-19 05:28:50', '2019-10-19 05:34:51', NULL);
