INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (293, NULL, 'clinical_exams', 'cle_s2_heart_sound', '<p>Indicate the characteristics of the S2 Heart Sound</p>', NULL, '<p><a href="http://www.ncbi.nlm.nih.gov/books/NBK341/">EDUCATIONAL LINK FOR S2 HEART SOUND</a></p>', '<ul>
<li>S2 is usually louder and sharper than S1. (The popular "lub-dup" mnemonic of S1 and S2 accurately conveys this contrast in sounds.)</li>
<li>S2 occurs at the end of ventricular systole, and is the combination of aortic (A2) and pulmonic (P2) valve closure sounds.</li>
<li>At the end of ventricular ejection, blood flow has tapered off to the point where forward flow ceases, and then reverses itself.</li>
<li>This reversal of flow closes the semilunar valves, causing the second heart sound.</li>
<li>This closure sound is reflected back toward the cardiac base, where S2 is louder than S1.</li>
<li>When the aortic and pulmonic valves close asynchronously, the sound is called split S2.</li>
<li>Note however, that the opening snap of mitral stenosis occurs shortly after S2, and the S2-OS combination may be confused with a truly split S2. An S2-S3 combination may also be confused with split S2.</li>
</ul>', NULL, NULL, '', NULL, NULL, NULL, NULL, NULL, 723, '2016-09-14 15:57:00', '2018-01-06 06:37:21', NULL);
