INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (236, NULL, 'clinical_exams', 'cle_hepato_jugular_yn', '<ul>
<li>Indicate if a Hepato jugular Reflex is present</li>
</ul>', NULL, '<ul>
<li>Hepatojugular reflux is the distension of the neck veins precipitated by the maneuver of firm pressure over the liver.</li>
<li>It is seen in tricuspid regurgitation, heart failure due to other non-valvular causes, and other conditions including constrictive pericarditis, cardia tamponade, and inferior vena cava obstruction.</li>
<li>The HJR maneuver may be performed as follows:
<ul>
<li>The patient is positioned supine with elevation of the head at 45 degrees.</li>
<li>Look at jugular pulsations during quiet respirations (baseline JVP).</li>
<li>Apply gentle pressure (30-40 mm Hg) over the right upper quadrant or middle abdomen for at least 10 seconds (some suggest to 1 minute)</li>
<li>Repeat the JVP.</li>
<li>An increase in JVP of &gt;3 cm is a positive HJR test.</li>
</ul>
</li>
<li>Note: Normal subjects will have a decrease in JVP with this maneuver since venous return to the heart will be reduced. T</li>
<li>he jugular venous pressure may transiently rise and then return to normal or decrease within 10 seconds.</li>
<li>JAMA. 1957;165(3):281-284.</li>
<li>&nbsp;BMJ 1999;318:1172</li>
</ul>', NULL, 'JAMA. 1957;165(3):281-284.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 723, '2016-09-14 15:57:00', '2018-01-06 06:29:58', NULL);
