INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3209, NULL, 'tilt_tests', 'tilt_results', '<p>Indicate the&nbsp;Tilt&nbsp;&nbsp;Test results</p>
', NULL, NULL, '<ul>
	<li>The cornerstone of the test is an orthostatic challenge which is done with the upright tilt.</li>
	<li>Apart from its main use in the syncope workup, use of the test was described in the evaluation of the presence of autonomic neuropathy in a variety of conditions .</li>
	<li>The main idea behind the test is that reflex syncope is due to the abnormal cardiac autonomic reflexes, which lead to inappropriate vasodilatation (vaso-depressive reflex syncope), inappropriate bradycardia (cardio- inhibitory reflex syncope) or a mixed response .</li>
	<li>A prolonged upright position is a known trigger of reflex syncope, where, after an initial normal adaptation to standing, inappropriate vasodilatation or bradycardia appears, leading to symptoms.</li>
	<li>This is different from the orthostatic hypotension, where the initial response to standing is abnormal.</li>
</ul>
', 'http://dx.doi.org/10.4330/wjc.v8.i3.277', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 869, '2016-12-18 07:34:40', '2016-12-18 13:41:48', NULL);
