INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (484, NULL, 'acs_iv_meds', 'ufh_given', '<p>Indicate if unfractionated heparin was administered.</p>', '<p>Any occurrence between first medical contact and discharge</p>', NULL, '<ul>
<li>Exclude UFN doses given during CABG in OR.</li>
<li>Two types of heparins are commonly used as anticoagulants</li>
<li>Unfractionated heparin (UFH) and low molecular weight heparins (LMWHs).</li>
<li>UFH has been used for the prevention and treatment of thrombosis for several decades.</li>
<li>UFH has variable anticoagulant effects and pharmacological properties and also has limited bioavailability and highly variable anticoagulant response.</li>
<li>LMWHs are derived from UFH by depolymerization.</li>
<li>Each LMWH product has a specific molecular weight distribution that determines its anticoagulant activity and duration of action.</li>
<li>LMWHs are associated with a predictable dose&ndash;response and have fewer non-haemorrhagic side-effects.</li>
<li>Because of these clinical advantages, LMWHs have gradually replaced UFH for most indications.</li>
</ul>', 'ACTION Registry v2.4', NULL, 'IV Unfractionated Heparin', NULL, NULL, NULL, NULL, NULL, 682, '2016-09-14 15:57:00', '2018-06-12 13:02:06', NULL);
