INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (90, NULL, 'cath_reasons', 'cathr_peripheral_art_dis_yn', '<p>Indicate if the patient has a history of peripheral arterial disease (PAD) (includes upper and lower extremity, renal, mesenteric, and abdominal aortic systems).</p>', NULL, '<p><strong>Peripheral arterial disease can include:</strong></p>
<ul>
<li>1. Claudication, either with exertion or at rest.&nbsp;</li>
<li>2. Amputation for arterial vascular insufficiency.</li>
<li>3. Vascular reconstruction, bypass surgery, or percutaneous intervention to &nbsp;the extremities (excluding dialysis fistulas and vein stripping).</li>
<li>4. Documented aortic aneurysm with or without repair.&nbsp;</li>
<li>5. Positive non-invasive test (e.g., ankle brachial index &lt;=0.9); ultrasound,&nbsp;magnetic resonance, computed tomography, or angiographic imaging of &gt;&nbsp;50% diameter stenosis in any peripheral artery (e.g., renal, subclavian,&nbsp;femoral, iliac).</li>
<li>For purposes of the Registry, peripheral arterial disease excludes disease in&nbsp;the carotid and cerebrovascular arteries.</li>
<li>Source: ACC Clinical Data Standards, The Society of Thoracic Surgeons</li>
</ul>
<p>&nbsp;</p>', NULL, 'CathPCI Registry v4.4', NULL, 'Peripheral Arterial Disease', NULL, NULL, NULL, NULL, NULL, 717, '2016-09-14 15:57:00', '2018-06-14 15:19:11', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2859, NULL, 'echo_ivc', 'ivc_size', '<p>Indicate the Size of Inferior Vena Cava</p>
', NULL, NULL, '<ul>
	<li>The IVC is a thin-walled compliant vessel that adjusts to the body&#39;s volume status by changing its diameter depending on the total body fluid volume.</li>
	<li>The vessel contracts and expands with each respiration.</li>
	<li>Negative pressure created by the inspiration of the patient increases venous return to the heart, briefly collapsing the IVC.</li>
	<li>Exhalation decreases venous return and the IVC returns to its baseline diameter.</li>
	<li>Caval index: IVC expiratory diameter - IVC inspiratory diameter, divided by IVC expiratory diameter &times; 100 = caval index (%).</li>
	<li>The size of the inferior vena cava is best measured from a subcostal window shortly before it enters the right atrium.</li>
	<li>The normal size of the inferior vena cava is 15 to 17 mm, with a more than 50% size reduction during inspiration.</li>
	<li>Fluctuations in size can be assessed with 2D imaging and the MMode.</li>
	<li>The size of the vena cava inferior reflects the patient&#39;s fluid status and central venous pressure, and permits estimation of RA pressure.
	<ul>
		<li><em>Hypovolaemic shock</em>: &nbsp;Small diameter + high collapsibility index</li>
		<li><em>Cardiogenic shock</em>:&nbsp;IVC Large diameter + low collapsibility index</li>
		<li><em>Cardiac tamponade</em>&nbsp;Large diameter + low/absent collapsibility index&nbsp;</li>
		<li><em>Distributive shock &nbsp;</em>Small diameter + high collapsibility index&nbsp;</li>
		<li><em>Pulmonary embolism:</em> &nbsp;Large diameter + low collapsibility index; sludge ( mude&nbsp;) can be present</li>
		<li><em>Tension Pneumothorax:&nbsp;</em>Large diameter + low collapsibility index</li>
	</ul>
	</li>
</ul>
', NULL, NULL, NULL, NULL, NULL, '15 - 17mm', NULL, NULL, 846, '2016-10-18 08:07:20', '2016-10-18 08:25:21', NULL);
