INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (15, NULL, 'cor_anatomies', 'cor_anat_septal_ostium_type', 'Major Septal Perforator Anomalies', NULL, 'The reported incidence of anomalous origin of coronary arteries ranges from 0.6 to 1.2%.', 'The interventricular septum is considered the most densely vascularized portion of the heart, containing important elements of the cardiac conduction system and providing mechanical support for both right and left ventricular function. <br />Anatomically, this structure is perfused by the relatively small caliber anterior and posterior septal perforator branches arising from two large epicardial coronary vessels, the anterior interventricular (descending) branch of the left coronary artery and the posterior interventricular (descending) branch of the right coronary artery, respectively. In cases of significant atherosclerotic disease depriving flow in the major coronary vessels and their septal branches, an anomalous septal perforator artery can take over the task of supporting the function of the interventricular septum by supplementing essential collateral flow.', 'DOI: 10.1002/(SICI)1098-2353(1996)9:1<14::AID-CA3>3.0.CO;2-', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 929, '2019-07-17 06:08:24', '2019-07-19 05:19:23', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3573, NULL, 'cath_events', 'cath_ev_vasc_pseudoaneurysm_yn', '<p>Indocate If patient has had an&nbsp;Pseudoaneurysm formation</p>', NULL, NULL, '<p style="text-align: justify;">Small pseudoaneurysms, less than 3 cm, can&nbsp;typically be observed as they can clot spontaneously over time (within 4 weeks). Ultrasound&nbsp;guided compression can compress the site of&nbsp;communication between the pseudoaneurysm&nbsp;and the native artery and allow thrombosis of the aneurysmal sac. A FemoStop device can also be&nbsp;used to compress a pseudoaneurysm. Ultrasound&nbsp;guided percutaneous thrombin injection into the&nbsp;pseudoaneurysm can cause thrombosis of the<br />pseudoaneurysm within a few seconds after the&nbsp;injection. However, this method has the risk of&nbsp;thrombosis of the native artery and in some cases&nbsp;the femoral vein. Thrombin injection appears to&nbsp;be more effective than ultrasound-guided compression for femoral pseudoaneurysms. Other methods that have been investigated include paraaneurysmal saline where saline is injected into the&nbsp;communication between the false aneurysm and&nbsp;the native artery resulting in elimination of blood<br />flow. Endovascular techniques such as coil embolization, detachable balloons and stent-grafts have&nbsp;also been used to treat femoral pseudoaneurysms.&nbsp;Surgery is the gold standard treatment for pseudoaneurysms requiring treatment. Indications for<br />surgical treatment include rapid expansion of the&nbsp;pseudoaneurysm, concomitant distal ischemia or&nbsp;neurological deficit, mycotic pseudoaneurysm,&nbsp;failure of percutaneous intervention and compromised soft tissue viability. Surgical options include&nbsp;simply suturing the defect after hematoma evacuation or by patch angioplasty where surgical repair&nbsp;is achieved by using a patch&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 707, '2018-01-08 05:12:33', '2018-05-23 17:12:04', NULL);
