INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2341, NULL, 'cath_events', 'cath_ev_chf_new_post_yn', '<p>Indicate if the patient had new onset or acute recurrence of heart failure which necessitated new or increased pharmacologic therapy.</p>', NULL, '<p>&nbsp;<strong>Heart Failure:&nbsp;</strong></p>
<ul>
<li>A previous hospital admission with a principal diagnosis of heart failure is&nbsp;considered evidence of heart failure history&nbsp;</li>
<li>Heart failure is defined as physician documentation or report of any of the&nbsp;following clinical symptoms of heart failure:
<ul>
<li>unusual dyspnea on light exertion;&nbsp;</li>
<li>recurrent dyspnea occurring in the supine position;</li>
<li>fluid retention;</li>
<li>the&nbsp;description of rales,</li>
<li>jugular venous distension,</li>
<li>pulmonary edema on physical&nbsp;exam;&nbsp;or pulmonary edema on chest x-ray.</li>
<li>A previous hospital admission with&nbsp;principal diagnosis of heart failure is considered evidence of heart failure&nbsp;history.</li>
<li>A low ejection fraction without clinical evidence of heart failure does&nbsp;not qualify as heart failure.</li>
</ul>
</li>
</ul>
<p><strong>Source: ACC Data Standards, The Society of Thoracic Surgeons</strong></p>', '<ul>
<li>For patients with extended hospital stays, restrict coding of postprocedure events to 30 days after the last procedure.</li>
</ul>', 'CathPCI Registry v4.4', NULL, ' Heart Failure', NULL, NULL, NULL, NULL, NULL, 707, '2016-09-14 15:57:00', '2018-01-07 07:46:13', NULL);
