INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (683, NULL, 'electrocardiograms', 'ecg_lv_hypertrophy_yn', '<p>Indicate if there is on the ECG LV Hypertrophy</p>', NULL, '<p><strong>Suggested voltage criteria for LVH include:</strong></p>
<ul>
<li>The sum of the S wave in v1 or v2, PLUS the R wave in v5 or 6 &ge; 35mm, &nbsp; OR,</li>
<li>&nbsp;The sum of the deepest S wave + &nbsp;the tallest R wave &nbsp;&gt; 40m</li>
<li>&nbsp;Any single, R or S, wave in leads v1-v6 &nbsp;&ge; 45mm</li>
<li>&nbsp;The R wave in aVL &nbsp;&ge; 11mm</li>
<li>The R wave in lead I &ge; 12mm</li>
<li>&nbsp;The R wave in aVF &ge; 20mm</li>
</ul>', NULL, 'Circulation December 5, 2006', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 738, '2016-09-14 15:57:00', '2018-06-07 14:27:45', NULL);
