INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1494, NULL, 'functional_states', 'funs_frailty_scale_csha', '<p>Indicate the CSHA Clinical Frailty Scale score</p>', NULL, '<p><strong>Canadian Study of Health and Aging (CSHA)</strong>&nbsp;</p>
<ul>
<li>1 = Very fit: robust, active, energetic, well motivated &amp; fit; these people commonly exercise regularly &amp; are in the most fit group for their age</li>
<li>2 = Well: without active disease, but less fit than people in category 1</li>
<li>3 = Well, with treated comorbid disease: disease symptoms are well controlled compared with those in category 4</li>
<li>4 = Apparently vulnerable: although not frankly dependent, these people commonly complain of being "slowed up" or have disease symptoms</li>
<li>5 = Mildly frail: with limited dependence on others for instrumental activities of daily living</li>
<li>6 = Moderately frail: help is needed both with instrumental and non-instrumental activities of daily living</li>
<li>7 =Severely frail: completely dependent on others for the activities of daily living, or terminally ill</li>
</ul>
<hr />
<p><strong>Definition/criteria </strong></p>
<p><strong>Source:&nbsp;</strong>Updated standardized endpoint definitions for transcatheter aortic valve implantation: The Valve Academic Research Consortium-2 consensus document*</p>
<ul>
<li>Slowness, weakness, exhaustion, wasting and&nbsp;malnutrition, poor endurance and inactivity,&nbsp;loss of independence</li>
</ul>
<p><strong>Criteria:&nbsp;</strong></p>
<ul>
<li>5 m walking time*&nbsp;</li>
<li>Grip strength*&nbsp;</li>
<li>BMI&lt;20 kg/m2 and/or weight loss 5 kg/year&nbsp;</li>
<li>Serum albumin&lt;3.5 g/dL&nbsp;</li>
<li>Cognitive impairment or dementia</li>
</ul>
<p>&nbsp;</p>', NULL, 'TAVI Dataset BCSI', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 899, '2016-09-14 15:57:00', '2018-06-20 14:50:22', NULL);
