INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (330, NULL, 'cath_lvs', 'cath_lv_ef_val', '<p>Indicate the percentage of the blood emptied from the left ventricle at the end of the contraction.</p>', NULL, '<p><strong>Left Ventricular Ejection Fraction (LVEF):</strong>​&nbsp;</p>
