INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1382, NULL, 'acs_first_contacts', 'fc_mi_acute_symptoms_yn', '<p>Indicate the Presence of acute symptoms of myocardial ischemia, such as chest, upper extremity, mandibular, or epigastric discomfort, or an ischemic equivalent such as dyspnea or fatigue. This is one of the non-cardiac marker criteria supporting the diagnosis of acute myocardial infarction types 1, 2, and 4a.</p>', NULL, '<p>REF:</p>
<ul>
<li>2012 ACCF/AHA/ACP/AATS/PCNA/SCAI/STS</li>
<li>J Am Coll Cardiol 60:1-18, 2012.</li>
</ul>', '<p>Yes: The affirmative response to a question. (NCI C49488)</p>
<p><strong>TYPES OF MYOCARDIAL INFARCTION</strong></p>
<ul>
<li>Type 1 MI (spontaneous MI) is related to atherosclerotic plaque rupture or other event leading to thrombus formation in &ge; 1 of the coronary arteries, leading to decreased myocardial blood flow with ensuing necrosis;</li>
<li>Type 2 MI arises from a condition other than CAD;</li>
<li>Type 3 MI is deemed to have occurred when cardiac death occurs with symptoms suggestive of myocardial ischemia, but without biomarker values having been obtained; and</li>
<li>Type 4 and 5 MIs are related to PCI and CABG, respectively, and have been redefined since 2007.</li>
</ul>', 'J Am Coll Cardiol 60:1-18, 2012.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 816, '2016-09-14 15:57:00', '2018-06-23 09:17:01', NULL);
