INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2593, NULL, 'echo_ventricle_lv', 'elv_lvot_vti_val', '<ul>
<li>Left ventricular outflow tract Velocity-time integral by US.doppler</li>
</ul>
<p>&nbsp;</p>', NULL, NULL, '<ul>
<li>A velocity time integral is the integral of all of the velocities during the time of flow across a valve or in a vessel. &nbsp;</li>
<li>During a flow period, the doppler will measure all of the velocities along its scan line(CWD) or at the location of the sample volume (PWD). &nbsp;</li>
<li>The integral of all of the velocities during the time period yields a velocity-time-integral or VTI. &nbsp;</li>
<li>The VTI results in a measurement of length, cm.</li>
<li>The flow across a valve or in a vessel is not a flat flow profile.</li>
<li>&nbsp;The flow is parabolic. &nbsp;Therefore, the average velocity calculated with this technique may not represent the true cross-sectional velocity. &nbsp;</li>
<li>Clinically, this error tends to be dwarfed by the potential error associated with calculating the cross sectional area.<br />&nbsp;</li>
</ul>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 850, '2016-10-04 16:20:18', '2018-09-28 14:39:09', NULL);
