INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2905, NULL, 'echo_valve_mv', 'wilkins_score_val', '<p>Indicate the Wilkins Echo score Value</p>
', NULL, NULL, '<p><strong>The Wilkins Echo score can predict the procedural success for mitral valvuloplasty based on valve structure.</strong></p>

<ul>
	<li>A score of &le; 8 predicts a more favorable procedural, short, intermediate and long-term outcome (including survival).
	<ul>
		<li>Mobility 1- 4
		<ul>
			<li>Highly mobile, only tips restricted</li>
			<li>Normal mobility of base and mid-leaflet&nbsp;</li>
			<li>Valve moves forward in diastole mainly from the base&nbsp;</li>
			<li>No or minimal movement<br />
			&nbsp;</li>
		</ul>
		</li>
		<li>Leaflet thickening 1-4&nbsp;
		<ul>
			<li>Near-normal&nbsp;</li>
			<li>Thickening mainly at tips&nbsp;</li>
			<li>Thickening (5 - 8 mm) over the whole leaflet&nbsp;</li>
			<li>Severe thickening (&gt;8 mm) of whole leaflet<br />
			&nbsp;</li>
		</ul>
		</li>
		<li>Subvalvar thickening 1-4&nbsp;
		<ul>
			<li>Minimal just below leaflets&nbsp;</li>
			<li>Over one-third the chordae&nbsp;</li>
			<li>Extending to the distal third of the chordae&nbsp;</li>
			<li>Extensive thickening and shortening of the whole chord<br />
			&nbsp;</li>
		</ul>
		</li>
		<li>Calcification 1 - 4
		<ul>
			<li>A single area of echogenicity&nbsp;</li>
			<li>Scattered areas at leaflet margin&nbsp;</li>
			<li>Echogenicity extending to midportion of leaflets&nbsp;</li>
			<li>Extensive echogenicity over whole leaflet &nbsp;</li>
		</ul>
		</li>
	</ul>
	</li>
</ul>

<p><cite>SOURCE REF:&nbsp;British Heart Journal 1988, 60 (4): 299-308</cite></p>

<p><strong><big>Wilkins score, have many limitations </big></strong></p>

<ul>
	<li>Echocardiography limited in ability to differentiate nodular fibrosis from calcification.&nbsp;</li>
	<li>Assessment of commissural involvement is not included or underestimated.</li>
	<li>Doesn&rsquo;t account for uneven distribution of pathologic abnormalities.</li>
	<li>Doesn&rsquo;t account for relative contribution of each variable (no weighting of variables).</li>
	<li>Frequent underestimation of subvalvular disease.</li>
	<li>Doesn&rsquo;t use results from TEE or 3D echocardiography.</li>
</ul>

<p><cite>SOURCE:&nbsp;&nbsp;Heart. 2006;92(1):52&ndash;57.&nbsp;</cite></p>
', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 849, '2016-10-20 07:37:58', '2017-06-29 05:26:46', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1970, NULL, 'tavr_diagnostics', 'tavr_lv_edd_pre', '<p>Indicate the pre-procedure left ventricular internal diastolic dimension in cm. If more than one LV internal diastolic diameter is available, code the value determined by echocardiography.</p>', '<p>The last value between 12 months prior to the procedure and start of the procedure</p>', '<p>&nbsp;<strong>General Principles for Linear and Volumetric LV Measurements:</strong></p>
<ul>
<li>To obtain accurate linear measurements of interventricular septal wall thickness (SWT), posterior wall thickness (PWT), and LV internal dimension, recordings should be made from the parasternal long-axis acoustic window. It is recommended that LV internal dimensions (LVIDd and LVIDs, respectively) and wall thicknesses be measured at the level of the LV minor axis, approximately at the mitral valve leaflet tips.</li>
<li>These linear measurements can be made directly from 2D images or using 2D-targeted M-mode echocardiography.</li>
<li>
<p>Source: Recommendations for Chamber Quantification: A Report from the American Society of Echocardiography&nbsp; Guidelines and Standards Committee and the Chamber Quantification Writing Group, Developed in Conjunction with&nbsp; the European Association of Echocardiography, a Branch of the European Society of Cardiology, Journal of the&nbsp;&nbsp;&nbsp; American Society of Echocardiography. Volume 18 Number 12</p>
<p>&nbsp;</p>
</li>
</ul>
<p>&nbsp;</p>', '<ul>
<li>Using a 2D method, it is recommended that LV internal dimensions (LVIDd and LVIDs, respectively) be measured at the level of the LV minor dimension, at the mitral chordae level.</li>
</ul>
<p>&nbsp;</p>', 'TVT Registry  v2.0', NULL, 'Left Ventricular Internal Diastolic Dimension', NULL, NULL, NULL, NULL, NULL, 782, '2016-09-14 15:57:00', '2018-08-05 13:26:45', NULL);
