INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2124, NULL, 'tavr_post_evaluations', 'tavr_prost_av_reg_degree', '<p>Indicate the highest level of aortic regurgitation found on the echocardiogram.</p>', NULL, NULL, '<ul>
<li>Mild-to-moderate should be coded as moderate; moderate to severe should be coded as severe.</li>
</ul>
