INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (64, NULL, 'clinical_exams', 'cle_peri_rub_yn', '<p>Indicate if Pericardial rub (pericardial friction rub) is present&nbsp;</p>', NULL, NULL, '<p>Pericardial rub (pericardial friction rub) a scraping or grating noise heard with the heart beat, usually a to-and-fro sound, associated with an inflamed pericardium. Patients often complain of pleuritic discomfort that radiates in the chest, especially during inhalation. This condition must be distinguished from angina pectoris.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 723, '2016-09-14 15:57:00', '2018-01-06 06:40:32', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (875, NULL, 'cath_events', 'cath_ev_shock_start_yn', '<p>Indicate if the patient is in cardiogenic shock at the start of&nbsp; procedure.</p>', NULL, '<p>&nbsp;<strong>Cardiogenic shock is defined</strong>&nbsp;</p>
<ul>
<li>As a sustained&nbsp;(&gt;30 min) episode of hypoperfusion evidenced by systolic blood pressure &lt;90 mm Hg and/or,</li>
<li>if&nbsp;available, cardiac index &lt;2.2 L/min per square meter determined to be secondary to cardiac&nbsp;dysfunction and/or</li>
<li>the requirement for parenteral inotropic or vasopressor agents or mechanical&nbsp;support (e.g., IABP, extracorporeal circulation, VADs) to maintain blood pressure and</li>
<li>cardiac index&nbsp;above those specified levels.</li>
</ul>
<p>&nbsp;</p>', NULL, 'STS Adult Cardiac Surgery Database', NULL, 'Cardiogenic Shock', NULL, NULL, NULL, NULL, NULL, 707, '2016-09-14 15:57:00', '2018-05-01 03:55:12', NULL);
