INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (899, NULL, 'pci_procedures', 'pci_proc_syndax_score', '<p>Indicate the Syndax Score</p>', NULL, '<p><a href="http://www.syntaxscore.com/">SYNDAX SCORE WEBSITE</a></p>', '<p>&nbsp;<br /> The category based risk approach of the anatomical-based SYNTAX Score &shy;&ndash; i.e. &lsquo;low&rsquo;, &lsquo;intermediate,&rsquo; or &lsquo;high&rsquo; SYNTAX Scores &ndash; to guide decision making between CABG or PCI, has been shown to be potentially misleading in a post hoc analysis of the SYNTAX trial.<br /> Within this post hoc analysis, it was shown that low and high risk subjects existed in higher and lower SYNTAX Score tertiles, which appeared to have implications for the most appropriate revascularisation modality (CABG and PCI); e.g. there was a doubling of 3-year mortality in subjects with three-vessel disease (3VD)&shy;&shy;&ndash; with a low SYNTAX Score (&lt;23) and a high additive EuroSCORE (&ge;6) &shy;&ndash; who underwent PCI compared to CABG.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 770, '2016-09-14 15:57:00', '2018-01-06 04:47:07', NULL);
