INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (757, NULL, 'pace_cardiac_diagnosis', 'valvular_heart_primary', 'Indicate if the patient has a history of primary valvular heart disease that is moderately severe or severe.', 'Any occurrence between birth and the first generator procedure in this admission', '<p><strong>Primary Valvular Disease:</strong></p>

<p>Primary valvular heart disease is defined by heart disease that is primarily due to a valvular defect or<br />
abnormality, and is classified as:</p>

<ul>
	<li>Moderately severe or severe, or 3+ or 4+ aortic insufficiency.</li>
	<li>&nbsp;Moderately severe or severe, or 3+ or 4+ mitral insufficiency with echocardiographic evidence that mitral&nbsp;insufficiency is a primary abnormality and not secondary to ventricular dilation.</li>
	<li>Moderately severe or severe aortic stenosis defined by estimated aortic valve area by catheterization or&nbsp;Doppler echocardiography of &lt;=1.0 cm2.</li>
	<li>&nbsp;Moderately severe or severe mitral stenosis defined by estimated mitral valve area by catheterization or&nbsp;Doppler echocardiography of &lt;1.0 cm2.</li>
	<li>Moderately severe or severe pulmonic or tricuspid valve disease that is known to be a primary abnormality.</li>
</ul>

<p><strong><span style="background-color:#FFFF00">Source: ACC/AHA/HRS 2006 Key Data Elements and Definitions for Electrophysiological Studies and&nbsp;Procedures</span></strong><br />
&nbsp;</p>
', '<ul>
	<li><strong>Valve disease</strong> that is felt to be significant but does not fulfill the definition for primary valvular heat disease is&nbsp;considered contributory valvular heart disease, and should be coded as &#39;No.&#39;</li>
	<li>An example of primary valvular heart disease is severe aortic stenosis that caused heart failure or&nbsp;cardiomyopathy.&nbsp;Aortic stenosis is the primary problem.</li>
	<li>An example of secondary valvular heart disease is a patient who had a myocardial infarction that caused a&nbsp;valve defect, thus the valve disease is not primary.</li>
</ul>
', 'ICD Registry V2.1', NULL, ' Primary Valvular Heart Disease', NULL, NULL, NULL, NULL, NULL, 751, '2016-09-14 15:57:00', '2016-09-14 15:56:26', NULL);
