INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1304, NULL, 'pci_procedures', 'pci_proc_dev_first_activ_tm', '<p>Indicate the time the first device was activated regardless of type of device used.</p>', '<p>The first value on First Device Activation Date</p>', NULL, '<p><strong>Indicate the time (hours:minutes) using the military 24-hour clock, beginning at midnight (0000 hours).</strong></p>
<ul>
<li>Use the earliest time from the following:
<ul>
<li>1. Time of the first balloon inflation.</li>
<li>2. Time of the first stent deployment.</li>
<li>3. Time of the first treatment of lesion (AngjoJet or other thrombectomy/aspiration device, laser, rotational atherectomy).</li>
<li>4. If the lesion cannot be crossed with a guidewire or device (and thus none of the above apply), use the time of guidewire introduction.</li>
</ul>
</li>
<li>This is a process measure about the timeliness of treatment.</li>
<li>It is NOT a&nbsp;clinical outcomes measure based on TIMI flow or clinical reperfusion.</li>
<li>It&nbsp;does not matter whether the baseline angiogram showed TIMI 3 flow or if&nbsp;the final post-PCI angiogram showed TIMI 0 flow.</li>
<li>What is being&nbsp;measured is the time of the first mechanical treatment of the culprit lesion,&nbsp;not the time when TIMI 3 flow was (or was not) restored.&nbsp;</li>
</ul>', 'CathPCI Registry v4.4', NULL, 'First Device Activation Time', NULL, NULL, NULL, NULL, NULL, 770, '2016-09-14 15:57:00', '2018-06-25 13:04:25', NULL);
