INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1287, NULL, 'proc_histories', 'proch_cardiac_surg_other_yn', '<ul>
