INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2701, NULL, 'echo_ventricle_lv', 'elv_lv_speckle_long_strain', '<p>Indicate the speckle tracking echo (ste) longitudinal strain</p>', NULL, NULL, '<p>&nbsp;</p>
<ul>
<li>&nbsp;In speckle tracking, complex image analysis software identifies features of the image (i.e. bright speckles, as indicated by the green dot) and follows them over&nbsp;time from frame to frame.</li>
<li>Analysing the displacement of several speckles over time allows to calculate motion, velocity, strain, and strain rate</li>
<li>Echocardiographic strain imaging, also known as deformation imaging, has been developed as a means to objectively quantify regional myocardial function.</li>
<li>First introduced as post-processing of tissue Doppler imaging velocity converted to strain and strain rate, strain imaging has more recently also been derived from digital speckle tracking analysis.</li>
<li>Strain imaging has been used to gain greater understanding into the pathophysiology of cardiac ischemia and infarction, primary diseases of the myocardium, and the effects of valvular disease on myocardial function, and to advance our understanding of diastolic function.</li>
<li>Strain imaging has also been used to quantify abnormalities in the timing of mechanical activation for heart failure patients undergoing cardiac resynchronization pacing therapy.&nbsp;</li>
<li>Echocardiographic strain imaging, also known as deformation imaging, is a technological advancement that has been developed as a means to objectively quantify regional myocardial function&nbsp;</li>
<li>The term strain applied to echocardiography in a simplistic sense is to describe lengthening, shortening, or thickening, also known as regional deformation</li>
</ul>
<p><a href="http://dx.doi.org/10.1016/j.jacc.2011.06.038">LINK&nbsp;Echocardiographic Assessment of Myocardial Strain</a></p>', NULL, NULL, NULL, 'GLS', NULL, '18 - 20', NULL, NULL, 850, '2016-10-11 14:12:27', '2018-09-28 14:32:09', NULL);
