INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3231, NULL, 'tilt_tests', 'tilt_test_has', '<p>Indicate if patient has had a Tilt Test</p>
', NULL, NULL, '<ul>
	<li>Tilt table testing has not been evaluated in randomized controlled trials, and the clinical utility of the procedure has not been definitely established.</li>
	<li>The pretest probability of neurocardiogenic syncope is high in a patient without evidence of ischemia or structural heart disease, and even if the test is negative, neurocardiogenic syncope remains the most likely diagnosis.</li>
	<li>The results of testing therefore contribute little to establishing a diagnosis.</li>
	<li>Despite the lack of strong evidence, tilt table testing has become an established procedure in the clinical evaluation of patients with syncope when the cause cannot be established based on a detailed history and physical examination and routine diagnostic testing.</li>
	<li>The procedure is also widely used when the cause of syncope has been established but the results of tilt table testing will contribute to evaluation of near syncope, frequent falls, evaluation of therapy to prevent syncope recurrence, and evaluation of syncope related to neuropathies or dysautonomias.</li>
	<li>Other emerging conditions for which tilt table testing has been proposed include evaluation of chronic fatigue syndrome to determine if neurally mediated hypotension and bradycardia are contributing factors, and evaluation of recurrent vertigo and recurrent transient ischemic attacks.</li>
	<li>The use of tilt table testing for these indications has not gained widespread acceptance, and the diagnostic utility of tilt table testing to evaluate these conditions has not been established in the published medical literature.</li>
</ul>
', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 869, '2016-12-19 07:07:34', '2016-12-19 07:17:09', NULL);
