INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (281, NULL, 'info_coronaries', 'info_cor_diagnostic_status', '<p>Indicate the status of the diagnostic catheterization. The status is determined when the decision is made to activate the cath lab.</p>', '<p>The highest value on current procedure</p>', 'Salvage: The procedure is a last resort. The patient is in cardiogenic shock when the&nbsp; PCI begins (i.e., the time at which the first guide wire or intracoronary device is&nbsp; introduced into a coronary artery or bypass graft for the purpose of mechanical revascularization) OR within the last ten minutes prior to the start of the case or during the diagnostic portion of the case, the patient has also received chest compressions or has been on unanticipated circulatory support (e.g., intra-aortic&nbsp; balloon pump, extracorporeal membrane oxygenation, or cardiopulmonary support).', '<div>. Salvage: The procedure is a last resort. The patient is in cardiogenic shock when the &nbsp;PCI begins (i.e., the time at which the first guide wire or intracoronary device is &nbsp;introduced into a coronary artery or bypass graft for the purpose of mechanical revascularization) OR within the last ten minutes prior to the start of the case or during the diagnostic portion of the case, the patient has also received chest compressions or has been on unanticipated circulatory support (e.g., intra-aortic &nbsp;balloon pump, extracorporeal membrane oxygenation, or cardiopulmonary support).</div>', 'Draft Definitions for CDISC August 20, 2014', NULL, 'Diagnostic Cath Status', NULL, NULL, NULL, NULL, NULL, 916, '2016-09-14 15:57:00', '2019-09-28 03:47:51', NULL);
