INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (645, NULL, 'cath_clinicals', 'arrest_cardiac_24h_yn', '<p>Indicate if the patient has had an episode of cardiac arrest within 24 hours of procedure.</p>', '<p>Any occurrence between 24 hours prior to current procedure and up to current procedure</p>', NULL, '<p><strong>Cardiac arrest includes</strong></p><p>Pulseless clinical scenarios that can be brady arrests or tachy arrests requiring cardiopulmonary resuscitation (requiring two or more chest compressions, or open chest massage) and/or requiring emergency defibrillation.</p><p>TVT Registry&nbsp;v2.0&nbsp;Seq. #: 5035 Name: Cardiac Arrest w/in 24 Hours</p>', 'CathPCI Registry v4.4', NULL, 'Cardiac Arrest w/in 24 Hours', NULL, NULL, NULL, NULL, NULL, 705, '2016-09-14 15:57:00', '2019-10-19 15:27:25', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2187, NULL, 'cath_clinicals', 'bag_mask_ventilation_yn', '<ul>	<li>Indicate if bag mask ventilation was used for airway management during the procedure.</li></ul>', NULL, NULL, NULL, 'IMPACT Registry v1.0.1', NULL, 'Airway Management - Bag mask ventilation', NULL, NULL, NULL, NULL, NULL, 705, '2016-09-14 15:57:00', '2019-10-19 15:28:18', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2336, NULL, 'sat_pressures', 'pcw_pres_a', '<p>Indicate The pcw pressure value at point a</p>', NULL, '<p><strong>Correlation to the ECG</strong></p><ul><li>The easiest wave to evaluate an atrial tracing is to first locate the v wave.</li><li>It will appear&nbsp;&nbsp;immediately after the T wave on a CVP waveform, however, it will be .08-.12 seconds&nbsp;after the T wave on a PAWP tracing.</li><li>You can generally identify the v wave by ruling out&nbsp;other waves.</li><li>It must be after the peak of the T wave.</li><li>Once the v wave is identified, the&nbsp;a and&nbsp;c can be determined.</li><li>Observe the ECG rhythm. If the patient has a sinus rhythm, an a wave should be&nbsp;present.</li><li>The a should be in the PR interval for a CVP.</li><li>It is later in the PAWP, appearing&nbsp;within or even after the QRS.</li><li>If the patient does not have a P wave, the a wave will be absent.</li><li>If the P is not&nbsp;synchronized to the QRS (i.e., retrograde P waves that occur simultaneous with or after&nbsp;the QRS), very large a waves may be present.</li><li>These large a waves may appear as&nbsp;one very large wave during a cardiac cycle.</li><li>The large a waves are called cannon a&nbsp;waves.</li><li>They are actually exaggerated atrial pressures that occur when the atria contract&nbsp;against a closed AV valve, adding to the pressure that is already being generated due to&nbsp;the c or v wave&nbsp;</li></ul>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 713, '2016-09-14 15:57:00', '2019-10-01 05:42:52', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1182, NULL, 'functional_states', 'glasgow_coma_scale', 'Glasgow Coma Scale on arrival in cath lab', NULL, '<p><strong><span style="background-color: #ffff00;">Glasgow Coma Scale: </span></strong></p><ul><li>While the 15-point scale is the predominant one in use, this is in fact a modification and is more correctly referred to as the Modified Glasgow Coma Scale.</li><li>The original scale was a 14-point scale, omitting the category of "abnormal flexion".</li><li>Some centres still use this older scale, but most (including the Glasgow unit where the original work was done) have adopted the modified one.&nbsp;The Rappaport Coma/Near Coma Scale made other changes.</li></ul><p><a href="http://www.sfar.org/scores2/glasgow2.php">WEB SITE TO CALCULATE GLASGOW COMA SCALE</a></p>', '<p><strong>EYE OPENING</strong></p><ul><li>None 1 = Even to supra-orbital pressure To pain</li><li>2 = Pain from sternum/limb/supra-orbital pressure To speech</li><li>3 = Non-specific response, not necessarily to command Spontaneous</li><li>4 = Eyes open, not necessarily aware</li></ul><p><strong>MOTOR RESPONSE </strong></p><ul><li>None 1 = To any pain; limbs remain flaccid Extension</li><li>2 = Shoulder adducted and shoulder and forearm internally rotated Flexor response</li><li>3 = Withdrawal response or assumption of hemiplegic posture Withdrawal</li><li>4 = Arm withdraws to pain, shoulder abducts Localizes pain</li><li>5 = Arm attempts to remove supra-orbital/chest pressure Obeys commands</li><li>6 = Follows simple commands</li></ul><p><strong>EYE OPENING</strong></p><ul><li>None 1 = Even to supra-orbital pressure To pain</li><li>2 = Pain from sternum/limb/supra-orbital pressure To speech</li><li>3 = Non-specific response, not necessarily to command Spontaneous</li><li>4 = Eyes open, not necessarily aware</li></ul><p><strong>MOTOR RESPONSE</strong></p><ul><li>None 1 = To any pain; limbs remain flaccid Extension</li><li>2 = Shoulder adducted and shoulder and forearm internally rotated Flexor response</li><li>3 = Withdrawal response or assumption of hemiplegic posture Withdrawal</li><li>4 = Arm withdraws to pain, shoulder abducts Localizes pain</li><li>5 = Arm attempts to remove supra-orbital/chest pressure Obeys commands</li><li>6 = Follows simple commands</li></ul><p><strong>VERBAL RESPONSE</strong></p><ul><li>None 1 = No verbalization of any type Incomprehensible</li><li>2 = Moans/groans, no speech Inappropriate</li><li>3 = Intelligible, no sustained sentences Confused</li><li>4 = Converses but confused, disoriented Oriented</li><li>5 = Converses and oriented</li></ul><p><strong>VERBAL RESPONSE</strong></p><ul><li>None 1 = No verbalization of any type Incomprehensible</li><li>2 = Moans/groans, no speech Inappropriate</li><li>3 = Intelligible, no sustained sentences Confused</li><li>4 = Converses but confused, disoriented Oriented</li><li>5 = Converses and oriented Ref: Teasdale G, Jennett B. "Assessment of coma and impaired consciousness.</li></ul><p><em>A practical scale." The Lancet 13;2(7872):81-4, 1974.</em></p>', 'BCIS vers 5.6.2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 899, '2016-09-14 15:57:00', '2022-05-04 12:13:34', NULL);
