INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (222, NULL, 'blood_samples', 'bs_hb_init_val', '<p>Indicate the initial hemoglobin (HGB) value in g/dL.</p>', '<p>The first value between first medical contact and discharge</p>', 'Hemoglobin (Hb or Hgb) is the iron-containing oxygen-transport metalloprotein in the red blood cells. It carries oxygen from the lungs to the rest of the body (i.e. the tissues) where it releases the oxygen to burn nutrients and provide energy. <br /><br />Hemoglobin concentration measurement is among the most commonly performed blood tests, usually as part of a complete blood count. If the concentration is below normal, this is called anemia.<br /><br />Anemias are classified by the size of red blood cells: "microcytic" if red cells are small,<br />"macrocytic" if they are large, and "normocytic" if otherwise. <br /><br />Dehydration or hyperhydration can greatly influence measured hemoglobin levels.', NULL, 'http://s.details.loinc.org/LOINC/718-7.html?sections=Simple', NULL, 'Initial Hemoglobin Value', NULL, NULL, NULL, NULL, NULL, 697, '2016-09-14 15:57:00', '2022-05-05 08:04:55', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1488, NULL, 'cath_radiations', 'cath_fluoro_time', '<p>Indicate the total fluoroscopy time recorded to the nearest 0.1 minute.</p>', '<p>The total between start of the procedure and end of the procedure</p>', NULL, '<p>Please collect Fluoroscopy Time, Reference Air Kerma and Kerma Area Product values, if available.</p>', 'TVT Registry', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 716, '2016-09-14 15:57:00', '2022-05-05 19:49:57', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3429, NULL, 'core_histories', 'prior_stroke_yn', '<p>Indicate if the patient was diagnosed with a stroke.</p>', '<p>&nbsp;Any occurrence between start of procedure and until next procedure or discharge</p>', '<p>An ischemic stroke is an acute episode of focal or global neurological dysfunction caused by brain, spinal<br />cord, or retinal vascular injury as a result of infarction of central nervous system tissue. Hemorrhage may be<br />a consequence of ischemic stroke. In this situation, the stroke is an ischemic stroke with hemorrhagic<br />transformation and not a hemorrhagic stroke. A hemorrhagic stroke is defined as an acute episode of focal or<br />global cerebral or spinal dysfunction caused by intraparenchymal, intraventricular, or subarachnoid<br />hemorrhage (note: subdural hematomas are intracranial hemorrhagic events and not strokes).<br /><cite>Source: Hicks KA, Tcheng JE, Bozkurt B, et al. 2014 ACC/AHA Key Data Elements and Definitions for<br />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cardiovascular Endpoint Events in Clinical Trials: A Report of the American College of<br />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cardiology/American Heart Association Task Force on Clinical Data Standards (Writing Committee<br />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; to Develop Cardiovascular Endpoints Data Standards). J Am Coll Cardiol. 2015;():.<br />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Doi:10.1016/j.jacc.2014.12.018.</cite><br />&nbsp;</p>', NULL, 'NCDR  ICD Registry  v2.2', NULL, ' Stroke', NULL, NULL, NULL, NULL, NULL, 732, '2017-03-02 18:46:54', '2022-05-04 15:37:55', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (243, NULL, 'functional_states', 'funs_richmond_agitation_scale', 'Indicate the Agitation State of the Patient (Richmond level)', NULL, '1. Observe patient. Is patient alert and calm (score 0)?<br />2. Does patient have behavior that is consistent with restlessness or agitation?<br />Assign score +1 to +4 using the criteria listed above.<br />3. If patient is not alert, in a loud speaking voice state patient''s name and direct patient to open eyes and look at speaker. Repeat once if necessary. Can prompt patient to continue looking at speaker.<br />Patient has eye opening and eye contact, which is sustained for more than 10 seconds (score -1).<br />Patient has eye opening and eye contact, but this is not sustained for 10 seconds (score -2).<br />Patient has any movement in response to voice, excluding eye contact (score -3).<br />4. If patient does not respond to voice, physically stimulate patient by shaking shoulder and then rubbing sternum if there is no response.<br />Patient has any movement to physical stimulation (score -4).<br />Patient has no response to voice or physical stimulation (score -5).', '<strong>Sedation and Delirium in the Intensive Care Unit<br /></strong><br />Emphasis on sedation and analgesia in critically ill patients has recently come to the forefront of patient care in the ICU. <br />The primary goal is to achieve adequate pain control and anxiolysis with appropriate sedation and analgesia. <br />Unfortunately, in critically ill patients on mechanical ventilation, this is very challenging and can lead to oversedation, delirium, and harm to the patient.<br /><br />It is very important to titrate the medications used for analgesia and sedation in a goal-directed manner so as to safely provide comfort without impeding recovery, affecting weaning from the ventilator, and prolonging length of stay. Several sedation monitoring systems have been developed in the ICU, such as the Sedation Agitation Scale, the Motor Activity Assessment Scale, and the Richmond Agitation-Sedation Scale (RASS)', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 899, '2022-05-02 13:42:21', '2022-05-02 14:01:45', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3425, NULL, 'icd_post_proc_events', 'myocardial_infarction_yn', '<p>: Indicate if the patient had a myocardial infarction.</p>', '<p>Any occurrence between start of procedure and until next procedure or discharge</p>', '<p><strong>&nbsp;Myocardial Infarction/Prior MI</strong></p><p>Criteria for acute myocardial infarction:<br />The term acute myocardial infarction (MI) should be used when there is evidence of myocardial necrosis in a<br />clinical setting consistent with acute myocardial ischemia. Under these conditions any one of the following<br />criteria meets the diagnosis for MI:<br />- Detection of a rise and/or fall of cardiac biomarker values [preferably cardiac troponin (cTn) with at least<br />one value above the 99th percentile upper reference limit (URL) and with at least one of the following:<br />Symptoms of ischemia.<br />New or presumed new significant ST-segment-T wave (ST-T) changes or new left bundle branch block (LBBB).<br />Development of pathological Q waves in the ECG.<br />Imaging evidence of new loss of viable myocardium or new regional wall motion abnormality. Identification<br />of an intracoronary thrombus by angiography or autopsy.<br />- Cardiac death with symptoms suggestive of myocardial ischemia and presumed new ischemic ECG changes<br />or new LBBB, but death occurred before cardiac biomarkers were obtained, or before cardiac biomarker<br />values would be increased.<br />- Percutaneous coronary intervention (PCI) related MI is arbitrarily defined by elevation of cTn values (&gt;5 x<br />99th percentile URL) in patients with normal baseline values (99th percentile URL) or a rise of cTn values<br />&gt;20% if the baseline values are elevated and are stable or falling. In addition, either (i) symptoms suggestive<br />of myocardial ischemia or (ii) new ischemic ECG changes or (iii) angiographic findings consistent with a<br />procedural complication or (iv) imaging demonstration of new loss of viable myocardium or new regional wall<br />motion abnormality are required.<br />- Stent thrombosis associated with MI when detected by coronary angiography or autopsy in the setting of<br />myocardial ischemia and with a rise and/or fall of cardiac biomarker values with at least one value above the<br />99th percentile URL.<br />- Coronary artery bypass grafting (CABG) related MI is arbitrarily defined by elevation of cardiac biomarker<br />values (&gt;10 x 99th percentile URL) in patients with normal baseline cTn values (99th percentile URL). In<br />addition, either (i) new pathological Q waves or new LBBB, or (ii) angiographic documented new graft or new<br />native coronary artery occlusion, or (iii) imaging evidence of new loss of viable myocardium or new regional<br />wall motion abnormality.<br />Any one of the following criteria meets the diagnosis for prior MI:<br />- Pathological Q waves with or without symptoms in the absence of non-ischemic causes.<br />- Imaging evidence of a region of loss of viable myocardium that is thinned and fails to contract, in the<br />absence of a non-ischemic cause.<br />- Pathological findings of a prior MI.<br /><cite>Source: Thygesen K, Alpert JS, Jaffe AS, et al. Third Universal Definition of Myocardial Infarction. J Am Coll &nbsp; Cardiol. 2012;60(16):1581-1598. doi:10.1016/j.jacc.2012.08.001.</cite><br />&nbsp;</p>', NULL, 'doi:10.1016/j.jacc.2012.08.001.', NULL, 'Myocardial Infarction', NULL, NULL, NULL, NULL, NULL, 908, '2017-03-02 18:37:02', '2022-05-04 13:58:02', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (244, NULL, 'core_histories', 'heart_failure_prior', 'Indicate if there is a previous history of heart failure', 'Any occurrence between birth and arrival at first facility.', 'Heart failure is a complex clinical syndrome that results from any structural or functional impairment of ventricular filling or ejection of blood. <br /><br />The cardinal manifestations of HF are dyspnea and fatigue, which may limit exercise tolerance, and fluid retention, which may lead to pulmonary and/or splanchnic congestion and/or peripheral edema. <br /><br />Some patients have exercise intolerance but little evidence of fluid retention, whereas others complain primarily of edema, dyspnea, or fatigue. <br /><br />Because some patients present without signs or symptoms of volume overload, the term "heart failure" is preferred over "congestive heart failure." <br /><br />There is no single diagnostic test for HF because it is largely a clinical diagnosis based on a careful history and physical examination.', 'A previous hospital admission with principal diagnosis of heart failure is considered evidence of heart failure history.', 'doi:10.1016/j.jacc.2013.05.019', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 732, '2022-05-04 15:02:54', '2022-05-04 15:02:54', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1072, NULL, 'proc_histories', 'proch_cabg_prior_yn', 'Indicate if the patient had coronary artery bypass graft (CABG) surgery prior to this admission.', '<p>Any occurrence between arrival at this facility and discharge</p>', 'Coronary artery bypass graft surgery is when the native vessels of the heart are bypassed with other vessels (internal mammary artery, radial artery or saphenous vein) to restore normal blood flow to the obstructed coronary arteries.', 'Source: Cannon CP, Brindis RG, Chaitman BR, et al. 2013 ACCF&gt;AHA Key Date Elements and Definitions for Measuring the Clinical Management and Outcomes of Patients with Acute Coronary Syndromes and Coronary Artery Disease.', 'ACTION Registry v2.4', NULL, ' CABG', NULL, NULL, NULL, NULL, NULL, 919, '2016-09-14 15:57:00', '2022-05-04 15:16:29', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3703, NULL, 'core_histories', 'coh_tobacco_amount', '<p>Indicate the amount of cigarette smoking reported by the patient.</p>', '<p>The value on arrival at this facility</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 732, '2018-06-07 08:41:11', '2022-05-05 06:58:47', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1942, NULL, 'blood_samples', 'tavr_pre_inr_na', '<p>Indicate if the pre-procedure International Normalized Ratio (INR) was not drawm</p>', NULL, NULL, NULL, 'TVT Registry v2.0', NULL, 'INR Not Drawn', NULL, NULL, NULL, NULL, NULL, 697, '2016-09-14 15:57:00', '2022-05-05 08:12:59', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (917, NULL, 'cath_stresses', 'coronary_calcium_score_yn', '<p>Indicate if a coronary calcium score is available.</p>', '<p>The last value between 6 months prior to current procedure and current procedure</p>', NULL, NULL, 'CathPci Registry v4.4', NULL, 'Coronary Calcium Score', NULL, NULL, NULL, NULL, NULL, 709, '2016-09-14 15:57:00', '2022-05-05 19:58:31', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (949, NULL, 'acs_hospital_events', 'acs_he_shock_new_yn', '<ul><li>Indicate if the patient had a new onset or acute recurrence of cardiogenic shock in your facility.</li></ul>', '<p>Any occurrence between arrival at this facility and discharge</p>', '<p><strong>Cardiogenic Shock&nbsp;</strong></p><ul><li><strong><em>Cardiogenic shock is defined as&nbsp;</em></strong></li><li>1. a sustained (&gt;30 minutes) episode of systolic blood pressure &lt;90 mm Hg determined to be secondary to cardiac dysfunction, and/or</li><li>2. a sustained (&gt;30 minutes) episode of cardiac index &lt;2.2 L/min/m2 determined to be secondary to cardiac dysfunction, and/or</li><li>3. the sustained (&gt;30 minutes) requirement for parenteral inotropic or vasopressor agents or mechanical support (e.g., IABP, extracorporeal circulation, ventricular assist devices) to maintain systolic blood pressure &gt;=90 mm Hg, and/or</li><li>4. the sustained (&gt;30 minutes) requirement for parenteral inotropic or vasopressor agents or mechanical support (e.g., IABP, extracorporeal circulation, ventricular assist devices) to maintain CI &gt;=2.2 L/min/m2 Source: NCDR</li></ul>', '<p>Transient episodes of hypotension reversed with IV fluid or atropine do not constitute cardiogenic shock. The hemodynamic compromise (with or without extraordinary supportive therapy) must persist for at least 30 minutes.</p>', 'ACTION Registry  v2.4', NULL, 'Cardiogenic Shock', NULL, NULL, NULL, NULL, NULL, 681, '2016-09-14 15:57:00', '2022-05-04 17:37:17', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3430, NULL, 'icd_post_proc_events', 'transient_ischemic_attack', '<p>Indicate if the patient had a transient ischemic attack (TIA).</p>', '<p>&nbsp;Any occurrence between start of procedure and until next procedure or discharge</p>', '<p>Transient episode of focal neurological dysfunction caused by brain, spinal cord, or retinal ischemia without acute infarction.<br /><br /><cite><strong>Source:</strong> Hicks KA, Tcheng JE, Bozkurt B, et al. 2014 ACC/AHA Key Data Elements and Definitions for Cardiovascular Endpoint Events in Clinical Trials: A Report of the American College of &nbsp;Cardiology/American Heart Association Task Force on Clinical Data Standards (Writing Committee&nbsp; to Develop Cardiovascular Endpoints Data Standards). J Am Coll Cardiol.&nbsp; &nbsp; &nbsp; 2015;():.Doi:10.1016/j.jacc.2014.12.018.</cite><br />&nbsp;</p>', '<p>Persistence of symptoms is an acceptable indicator of acute infarction. If it is used, duration of symptom<br />persistence that will be used to distinguish between transient ischemia and acute infarction should be<br />defined for any clinical trial in which it is used.<br />&nbsp;</p>', 'NCDR  ICD Registry  v2.2', NULL, ' Transient Ischemic Attack (TIA)', NULL, NULL, NULL, NULL, NULL, 908, '2017-03-03 15:59:53', '2022-05-05 04:08:45', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (2438, NULL, 'electrocardiograms', 'ecg_findings', 'Indicate if the ECG findings demonstrated either new or presumed new ST-segment elevation, new left bundle branch block, or isolated posterior myocardial infarction prior to any procedures.', 'Any occurrence between first medical contact and discharge', 'Arrival at first facility refers to either the time of arrival at your facility or the time of arrival at the transferring facility.', NULL, 'ACC NCDR', NULL, 'ECG Findings', NULL, NULL, NULL, NULL, NULL, 738, '2016-09-14 15:57:00', '2022-05-05 07:12:05', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1689, NULL, 'blood_samples', 'bs_creatinine_init_val', '<p>Indicate the results of the initial creatinine sample in mg/dL.</p>', '<p>The first value between first medical contact and discharge</p>', 'Creatinine or creatine anhydride, is a breakdown product of creatine phosphate in muscle. The loss of water molecule from creatine results in the formation of creatinine.<br /><br />It is transferred to the kidneys by blood plasma, whereupon it is eliminated by glomerular<br />filtration and partial tubular excretion. <br /><br />Creatinine is usually produced at a fairly constant rate and measuring its serum level is a simple test. <br /><br />A rise in blood creatinine levels is observed only with marked damage to functioning nephrons; therefore this test is not suitable for detecting early kidney disease. <br /><br />Creatine and creatinine are metabolized in the kidneys, muscle, liver and pancreas.', '<ul><li>If patient was transferred in, data available from the transferring facility should take precedence.</li><li>The initial creatinine sample may be obtained either at this facility or at the transferring facility.</li></ul><p>&nbsp;</p>', 'http://s.details.loinc.org/LOINC/2160-0.html?sections=Simple', NULL, 'Initial Creatinine Value', NULL, NULL, NULL, NULL, NULL, 697, '2016-09-14 15:57:00', '2022-05-05 08:01:30', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1705, NULL, 'blood_samples', 'bs_a1c_init_val', '<p>Indicate the hemoglobin A1C percentage value.</p>', '<p>The first value between first medical contact and discharge</p>', '<ul><li><a href="https://ncit.nci.nih.gov/ncitbrowser/ConceptReport.jsp?dictionary=NCI_Thesaurus&amp;version=16.07d&amp;ns=NCI_Thesaurus&amp;code=C111207">Link To Thesaurus&nbsp;&nbsp;</a>Code C111207</li><li>A value which captures the amount of glycosylated hemoglobin (A1C) present in a sample of blood as a percentage of 100</li></ul><br />Currently (2010), four standardization protocols exist for measuring Hgb A1c:<br />1. IFCC - designated as a Reference Method or RM (http://www.ifcchba1c.net/)<br /><br />2. NGSP - the long standing protocol used in the US and most other countries since the DCCT study<br />(http://www.ngsp.org/factors.asp)<br /><br />3.JDS/JSCC - a protocol used in Japan, Spain and possibly other countries<br /><br />4. Swedish - used in Sweden at least Protocols 2-4 are known as Designated Comparison Methods (DCM) and have been connected to the Reference Method and each other through various regression equations.<br /><br /><br />Because of the high degrees of standardization within protocol it should no longer be necessary to specify a LOINC code with a method such as &ldquo;HPLC&rdquo;, &ldquo;electrophoresis&rdquo; or anything else. Analytical instruments will be designed so that an Hgb A1c result can be<br />traced back to a specific standardization protocol, so the important distinction will be the standardization protocol as described above&nbsp; and which will be carried in the method field.<br /><br />A meeting of instrument manufacturers (presumably including Japanese) in Milan, Italy, December 12, 2007, agreed (among other items) that:<br /><br />-All manufacturers should implement worldwide the traceability to the IFCC reference system for Hgb -A1c.<br /><br /><br />-All new instruments sold after January 1st, 2011 will report (as a result of an Hgb A1c test) both SI (mmol/mol &ndash; no decimals) and NGSP derived units (percentage &ndash; one decimal), in agreement with the ConsensusStatement.<br /><br />-Note they only committed to supporting protocol (1) and (2) Different countries are adopting the international harmonization recommendations in different ways. We have information from the NGSP that the US will continue to report only Hgb A1c/NGSP, with the unit percent &ndash; i.e., no change. In Great Britain, labs have already started to report all results both as Hgb A1c (NGSP) in % and Hgb A1c (IFCC) in mmol/mol. In Canada, they are awaiting a recommendation from an expert panel. <br />Any of these measures could be reported in the same units, but the convention for the<br />reporting Hgb A1c under the new IFCC protocol will be to use units of mmol/mol to avoid confusion between the DCCT/NGSP and the IFFCC protocol.<br /><br />LOINC has defined 59261-8 (Hemoglobin A1c/Hemoglobin.total in Blood) by IFCC protocol. These protocols produce different results when expressed in the same units. For example, the equivalent of Hgb A1c (NGSP) of 6.5% is Hgb A1c (IFCC) is 4.8%.<br />The NGSP web site (http://www.ngsp.org/factors.asp) suggests the use of alternate measures, such as glycated albumen, for patients with severe iron deficiency, dialysis patients, and those with SS SC CC because of over or under reading that can occur<br />with these interferences. It also describes the effect of abnormal hemoglobins on results of HbA1c by instrument.', '<p><strong>Preferred Name</strong>: Hemoglobin A1C to Hemoglobin Ratio Measurement</p><p><strong>Definition:</strong> The determination of the ratio of the glycosylated hemoglobin compared to total hemoglobin present in a sample. The measurement may be expressed as a ratio or percentage.</p>', 'https://s.details.loinc.org/LOINC/4548-4.html?sections=Comprehensive', NULL, 'Initial Hemoglobin A1c Value', NULL, NULL, NULL, '3.8, 6.4', NULL, 697, '2016-09-14 15:57:00', '2022-05-05 08:11:12', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (227, NULL, 'blood_samples', 'bs_inr_val', 'Indicate the international normalized ratio (INR) if the patient is on routine warfarin or coumadin therapy.', '<p>The first value between first medical contact and discharge</p>', 'The INR is specifically intented for evaluating protime results on patients stabilized on long term oral anticoagulant therapy. <br /><br />The INR is not appropriate to evalulate hemostatic function in patients with liver disease, for screening for hereditary factor deficiencies or acquired vitamin K deficiencies, or for routine preoperative screening; this should be evaluated on the normal range in seconds. <br /><br />INR is calculated by the equation, INR = (PTR) raised to the power of ISI, where ISI = International Sensitivity Index (assigned to each reagent thromboplastin). PTR = prothrombin time ratio (pat PT/pop mean PT). <br /><br />Computation of the INR of specific thromboplastin reagent should allow for uniformity in prothrombin time testing regardless of the reagent system or instrumentation used.', 'The indications for obtaining an INR value are:<br /><br />Bleeding diathesis in patients with coagulation factors deficiency (fibrinogen and factors II, V, VII, or X, or a combined deficiency) in the extrinsic pathway Disseminated intravascular coagulation (DIC) <br /><br />Baseline sample collection before starting anticoagulation<br /><br />Monitoring the efficacy and safety while the patients are on warfarin due to clinical conditions with an increased risk of thrombosis such as mechanical heart valves, persistent atrial fibrillation, venous thromboembolism, stroke, and peripheral arterial disease<br /><br />Test for liver synthetic function and to calculate the model for end-stage liver diseases (MELD) score in end-stage liver disease', 'http://s.details.loinc.org/LOINC/6301-6.html?sections=Simple', NULL, 'INR Value', NULL, NULL, NULL, NULL, NULL, 697, '2016-09-14 15:57:00', '2022-05-05 08:20:11', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1384, NULL, 'cath_clinicals', 'anesthitist_present_at_start_yn', 'Indicate if an anesthesiologist was present at the start of the procedure.', 'The value on current procedure', NULL, '<p>Code "Yes" if an anesthesiologist (MD) or nurse anesthetist was present at the start of the procedure. No Yes</p><p><span style="background-color: #ffa500;">&nbsp;IMPACT Registry&nbsp;v1.0.1&nbsp;Seq. #: 5060 Name: Anesthesiologist Present</span></p>', 'IMPACT Registry', NULL, 'Anesthesiologist Present', NULL, NULL, NULL, NULL, NULL, 705, '2016-09-14 15:57:00', '2022-05-05 19:51:07', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1385, NULL, 'cath_clinicals', 'cath_anesthitist_called_after_start_yn', 'Indicate if an anesthesiologist was called into the cath lab after the start of the procedure, due to a need to escalate care.', 'The value on current procedure', NULL, '<p>Code "Yes" if an anesthesiologist (MD) or nurse anesthetist was called into the procedure for escalation of care. No Yes</p>', 'IMPACT Registry', NULL, 'Anesthesiologist Called In', NULL, NULL, NULL, NULL, NULL, 705, '2016-09-14 15:57:00', '2022-05-05 19:53:24', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (905, NULL, 'cath_procedures', 'cath_proc_biopsy_yn', '<p>Indicate if patient had Myocardial Biopsy during this cath visit</p>', NULL, '<p>Removal of myocardial tissue for microscopic examination, usually taken from the right heart using a transvenous approach.</p><p><a href="http://http//ncit.nci.nih.gov/ncitbrowser/ConceptReport.jsp?dictionary=NCI_Thesaurus&amp;code=C51674&amp;ns=NCI_Thesaurus">NCI-Thesaurus</a></p>', NULL, 'Aux In House', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 715, '2016-09-14 15:57:00', '2022-05-27 19:16:17', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (149, NULL, 'cath_reasons', 'av_reguritation_degree', '<p>Indicate the grade of Aortic Reguritation</p>', NULL, '<p><strong>Chronic regurgitation </strong>has a prolonged course over a period of months to years. The left ventricle is able to compensate for volume overload initially but then decompensates with the appearance of clinical symptoms of congestive heart failure.</p>', '<p>Different criteria have been developed to diagnose patients based on Doppler echocardiographic and cardiac catheterisation measurements, to help time the surgery and ensure the benefits outweigh the perioperative risk (mortality 4%) and long-term complications of prosthetic valve.</p><p><strong>Compensated</strong></p><ul><li>Ejection fraction &gt;55%</li><li>Fractional shortening &gt;32%</li><li>End-diastolic diameter &lt;60 mm</li><li>End-systolic diameter &lt;45 mm</li><li>End-diastolic volume &lt;120 mL/m^2</li><li>End-systolic volume &lt;50 mL/m^2</li></ul><p><strong>Transitional</strong></p><ul><li>Ejection fraction 51% to 55%</li><li>Fractional shortening 30% to 31%</li><li>End-diastolic diameter 60-70 mm</li><li>End-systolic diameter 45-50 mm</li><li>End-diastolic volume 130-160 mL/m^2</li><li>End-systolic volume 50-60 mL/m^2</li></ul><p><strong>Decompensated</strong></p><ul><li>Ejection fraction &le;50%</li><li>Fractional shortening &lt;29%</li><li>End-diastolic diameter &gt;75 mm</li><li>End-systolic diameter &gt; 55 mm</li><li>End-diastolic volume &gt;170 mL/m^2</li><li>End-systolic volume &gt;60 mL/m^2</li></ul>', '￼BMJ Best Practice', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 717, '2016-09-14 15:57:00', '2022-05-05 19:55:33', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (918, NULL, 'cath_stresses', 'coronary_calcium_score_val', '<p>Indicate the coronary calcium score.</p>', '<p>The lasst value between 6 months prior to current procedure and current procedure</p>', '<ul><li>This procedure is performed to check for any plaque or calcium build up in the coronary arteries which causes heart disease or can lead to a heart attack.</li><li>Cardiac Scoring is a better predictor of coronary events than cholesterol screening or other risk factors.</li></ul>', NULL, 'CathPci Registry v4.4', NULL, 'Calcium Score', NULL, NULL, ' 0-4000', ' 0-6000', ': User', 709, '2016-09-14 15:57:00', '2022-05-05 19:57:22', NULL);
