INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (501, NULL, 'patients', 'patnts_zip_code', '<p>Indicate the patient''s Country Postal Service zip code of their primary residence.</p>', NULL, NULL, '<ul><li>If the patient does not have a residence, or is homeless, leave blank and check ''Zip Code NA''.</li></ul><p>ZIP code is a number consisted of five digits from 0 to 9. The USA is divided into geographical areas and the first digit of zip code represents one of these areas. There is a list of areas:</p><p>0 = Connecticut, Massachusetts, Maine, New Hampshire, New Jersey, Puerto Rico, Rhode Island, Vermont, Virgin Islands, Army Post Office Europe, Fleet Post Office Europe</p><p>1 = Delaware, New York, Pennsylvania</p><p>2 = District of Columbia, Maryland, North Carolina, South Carolina, Virginia, West Virginia</p><p>3 = Alabama, Florida, Georgia, Mississippi, Tennessee, Army Post Office Americas, Fleet Post Office Americas</p><p>4 = Indiana, Kentucky, Michigan, Ohio</p><p>5 = Iowa, Minnesota, Montana, North Dakota, South Dakota, Wisconsin</p><p>6 = Illinois, Kansas, Missouri, Nebraska</p><p>7 = Arkansas, Louisiana, Oklahoma, Texas</p><p>8 = Arizona, Colorado, Idaho, New Mexico, Nevada, Utah, Wyoming</p><p>9 = Alaska, American Samoa, California, Guam, Hawaii, Marshall Islands, Federated States of Micronesia, Northern Mariana Islands, Oregon, Palau, Washington, Army Post Office Pacific, Fleet Post Office Pacific</p><p>The next two digits of zip code represent a region in that geographical area. In other words, the first three digits stand for the sectional center facility. The fourth and fifth zip code digits indicate the area of the city or a village/town.</p>', 'CathPCI Registry v4.4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 757, '2016-09-14 15:57:00', '2022-05-20 04:33:35', NULL);
