INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (254, NULL, 'core_histories', 'coh_diabetes_status', 'Diabetes mellitus control status', NULL, 'The patient&rsquo;s diabetes mellitus is optimally controlled.<br /><br />Professional organizations have different treatment goal recommendations.<br />A target HbA1c level between 7.0% and 7.5% may be appropriate if it can be safely achieved in healthy older adults with few comorbidities and good functional status. Glycemic control<br />recommendations in older adults of 7.5% to 8.0% have been suggested.<br /><br />Higher HbA1c targets (8%&ndash;9%) are appropriate for older adults with multiple comorbidities, poor health, and limited life expectancy.', 'Garber AJ, Abrahamson MJ, Barzilay JI, et al. Consensus statement by the American Association of Clinical Endocrinologists and American College of Endocrinology on the comprehensive type 2 diabetes management algorithm - 2019 executive summary.<br />Endocr Pract. 2019;25:69-100.', 'Endocr Pract. 2019;25:69-100.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 732, '2022-05-21 16:12:19', '2022-05-21 16:13:20', NULL);
