INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (3424, NULL, 'icd_post_proc_events', 'icd_arrest_cardiac_yn', '<p>Indicate if the patient experienced cardiac arrest.</p>', '<p>Any occurrence between start of procedure and until next procedure or discharge</p>', '<p>"Sudden" Cardiac arrest is the sudden cessation of cardiac activity. The victim becomes unresponsive with no normal breathing and no signs of circulation.<br /><br />If corrective measures are not taken rapidly, this condition progresses to sudden death. <br /><br />Cardiac arrest should be used to signify an event as described above that is reversed, usually by CPR and/or defibrillation or cardioversion or cardiac pacing.<br /><br /><br /><cite>Source: ACCF/AHA 2011 Key Data Elements and Definitions of a Base Cardiovascular Vocabulary for<br />&nbsp; &nbsp; &nbsp; &nbsp; Electronic Health Records. JACC Vol. 58, No. 2, 2011 Weintraub et al. 203; July 5, 2011:202-22</cite><br />&nbsp;</p>', NULL, 'NCDR  ICD Registry  v2.2', NULL, 'Cardiac Arrest', NULL, NULL, NULL, NULL, NULL, 908, '2017-03-02 18:29:05', '2022-05-26 06:46:31', NULL);
