INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (271, NULL, 'research_terms', 'heart_failure_borderline_efpef_yn', 'Indicate: EF 41%&ndash;49%. These patients fall into a borderline or intermediate group.', NULL, 'HF is a complex clinical syndrome that results from any structural or functional impairment of ventricular<br />filling or ejection of blood. The cardinal manifestations of HF are dyspnea and fatigue, which may limit<br />exercise tolerance, and fluid retention, which may lead to pulmonary and/or splanchnic congestion and/or<br />peripheral edema. Some patients have exercise intolerance but little evidence of fluid retention, whereas others complain primarily of edema, dyspnea, or fatigue. Because some patients present without signs or<br />symptoms of volume overload, the term HF is preferred over congestive heart failure. There is no single<br />diagnostic test for HF because it is largely a clinical diagnosis based on a careful history and physical<br />examination.', '<p>Clinical syndrome characterized by abnormal cardiac systolic and/or diastolic function and resulting symptoms of low cardiac output of venous congestion.or congestion due to abnormal&nbsp;myocardial function.<br />&nbsp;</p>', 'Canadian Cardiovascular Society', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 775, '2016-09-14 15:57:00', '2022-05-26 19:36:49', NULL);
