INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1767, NULL, 'acs_oral_meds', 'acs_oral_meds_apixiban_at_home', '<p>Indicate if the patient has been taking apixiban routinely at home prior to this hospitalization</p>', '<p>Any occurrence between 2 weeks prior to first medical contact and first medical contact</p>', NULL, 'Apixaban is used help prevent strokes or blood clots in people who have atrial fibrillation (a condition in which the heart beats irregularly, increasing the chance of clots forming in the body and possibly causing strokes) that is not caused by heart valve disease.<br /><br />Apixaban is also used to prevent deep vein thrombosis (DVT; a blood clot, usually in the leg) and pulmonary embolism (PE; a blood clot in the lung) in people who are having hip replacement or knee replacement surgery. Apixaban is also used to treat DVT and PE and may be continued to prevent DVT and PE from happening again after the initial treatment is completed. Apixaban is in a class of medications called factor Xa inhibitors. It works by blocking the action of a certain natural substance that helps blood clots to form.', 'ACTION Registry v2.4', NULL, 'Apixiban at Home', NULL, NULL, NULL, NULL, NULL, 683, '2016-09-14 15:57:00', '2022-05-27 18:45:20', NULL);
