INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (544, NULL, 'cath_procedures', 'peripheral_interv_yn', '<p>Indicate if any peripheral Intervention was Performed in during this Lab Visit</p>', NULL, NULL, '<p style="text-align: left;"><strong>Peripheral Vascular Intervention (PVI)&nbsp;</strong></p><p style="text-align: left;">Peripheral Vascular Intervention (PVI): Peripheral vascular intervention 2 is a catheter-<br />based or open surgical procedure designed to improve arterial or venous blood flow or<br />otherwise modify or revise vascular conduits. Procedures may include, but are not limited to,<br />percutaneous transluminal balloon angioplasty, stent placement, thrombectomy,<br />embolectomy, atherectomy, dissection repair, aneurysm exclusion, treatment of dialysis<br />conduits, placement of various devices, intravascular thrombolysis or other<br />pharmacotherapies, and open surgical bypass or revision.<br />In general, the intention to perform percutaneous peripheral vascular intervention is denoted<br />by the insertion of a guide wire into a peripheral artery or vein.</p><p style="text-align: left;">The target vessel(s) and the type of revascularization procedure (e.g., surgical bypass,<br />thrombectomy, endarterectomy, percutaneous transluminal angioplasty, stent placement,<br />thromboembolectomy, and thrombolysis) should be specified and recorded. For the sake of<br />simplicity, this definition applies to the extracranial carotid artery and other non-cardiac<br />arteries and veins and excludes the intracranial vessels and lymphatics.</p><p style="text-align: left;">Draft Definitions for CDISC August 20, 2014</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 715, '2016-09-14 15:57:00', '2022-05-27 19:35:18', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (908, NULL, 'cath_procedures', 'pci_interv_yn', '<p>Indicate if the patient had a percutaneous coronary intervention (PCI).</p>', NULL, '<ul><li>PCI:</li><li>A percutaneous coronary intervention (PCI) is the placement of an angioplasty guide wire, balloon, or other device (e.g. stent, atherectomy, brachytherapy, or thrombectomy catheter) into a native coronary artery or coronary artery bypass graft for the purpose of mechanical coronary revascularization.</li><li>Source: NCDR</li></ul>', '<p><strong>Percutaneous Coronary Intervention (PCI):</strong></p><ul><li>Placement of an angioplasty guide wire,&nbsp;balloon, or other device (e.g., stent, atherectomy catheter, brachytherapy delivery device,&nbsp;or thrombectomy catheter) into a native coronary artery or coronary artery bypass graft&nbsp;for the purpose of mechanical coronary revascularization.</li><li>In the assessment of the&nbsp;severity of coronary lesions with the use of intravascular ultrasound, coronary flow&nbsp;reserve (CFR), or fractional flow reserve (FFR), insertion of a guide wire will NOT be&nbsp;considered PCI.</li></ul><p>Source CDISC July 3, 2014</p>', 'CathPCI Registry v4.4', NULL, 'PCI', NULL, NULL, NULL, NULL, NULL, 715, '2016-09-14 15:57:00', '2022-05-27 19:37:12', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1463, NULL, 'cath_procedures', 'proc_other_yn', '<p>Indicate if an "other procedure" was performed in conjunction with a left heart cath, diagnostic coronary angiography, or PCI procedure.</p>', NULL, NULL, '<ul><li>Other procedures include, but are not limited to right heart caths, EtOH ablations, septal closures, and other (renal, abdominal, peripheral or carotid) angiograms and/or endovascular interventions.</li><li>The intent of "other procedure" is to capture those procedures that would add additional fluoro time and/or contrast volume to the diagnostic cath and/or PCI procedure.</li><li>Do not code "other procedure" unless the procedure is performed in conjunction with a left heart cath, diagnostic coronary angiography, or PCI procedure.</li></ul>', 'CathPci Registry v4.4', NULL, 'Other Procedure (in conj w/Dx Cath or PCI)', NULL, NULL, NULL, NULL, NULL, 715, '2016-09-14 15:57:00', '2022-05-27 19:37:50', NULL);
