INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1645, NULL, 'blood_samples', 'bs_ldl_val', '<p>Indicate the low density lipoprotein (LDL) cholesterol value in mg/dL.</p>', '<p>The last value between 6 months before first medical contact and discharge (none)</p>', 'A small dense LDL fraction, reported as part of LDL Subclasses panel. <br /><br />Seven LDL subclasses and 3 mid-band fractions can be determined by linear polyacrylamide gel electrophoresis and densitometric scanning.', '<p>Lipids obtained within the first 24 hours of arrival at this facility should take precedence.<br />If greater than 24 hours after arrival, then enter the most recent values obtained (within six months) prior to arrival at this facility.<br /><br />If an exact value could not be determined by the lab because it is too high or low (e.g., &gt; 300 mg/dL, &lt; 20 mg/dL, or no value was<br />reported), do not indicate a value for this element and indicate "Yes" for Lipid Panel Value Out of Range.</p><p>&nbsp;</p>', 'ACTION Registry v2.4', NULL, 'LDL Cholesterol', NULL, NULL, NULL, NULL, NULL, 697, '2016-09-14 15:57:00', '2022-05-28 09:35:40', NULL);
INSERT INTO public.definitions (id, definition_id, table_name, name, coding_instructions, target_value, support_definition, notes, ref, vocabulary_en, title_name, short_name, default_value, usual_range, valid_range, data_source, defs_table_id, inserted_at, updated_at, defs_table_id_multi) VALUES (1646, NULL, 'blood_samples', 'bs_triglycerides_val', '<p>Indicate the triglycerides value in mg/dL.</p>', '<p>The last value between 6 months before first medical contact and discharge</p>', 'A triglyceride (TG, triacylglycerol, TAG, or triacylglyceride) is an ester derived from glycerol and three fatty acids. <br />There are many&nbsp; triglycerides, depending on the oil source, some are highly unsaturated, some less so. <br />Triglycerides are the main constituents of vegetable oil (typically more unsaturated) and animal fats (typically more saturated). <br />In humans, triglycerides are a mechanism for storing unused calories, and their high concentrations in blood correlates with the consumption of starchy and fatty foods.<br />High levels of triglycerides in the bloodstream have been linked to atherosclerosis and, by extension, the risk of heart disease and stroke.<br /><br />Diets high in carbohydrates, with carbohydrates accounting for more than 60% of the total energy intake, can increase triglyceride&nbsp; levels.', '<ul><li>Lipids obtained within the first 24 hours of arrival at this facility should take precedence.</li><li>If greater than 24 hours after arrival, then enter the most recent values obtained (within six months) prior to arrival at this facility.</li><li>If an exact value could not be determined by the lab because it is too high or low (e.g., &gt; 300 mg/dL, &lt; 20 mg/dL, or no value was&nbsp;reported), do not indicate a value for this element and indicate "Yes" for Lipid Panel Value Out of Range.&nbsp;</li><li>If the value is presented as a decimal, round up to the nearest whole number.</li></ul><p>&nbsp;</p>', 'ACTION Registry  v2.4', NULL, 'Triglycerides', NULL, NULL, NULL, NULL, NULL, 697, '2016-09-14 15:57:00', '2022-05-28 09:38:48', NULL);


