INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8846, 'cor_anat_septal_ostium_type', 'lad_septal', 'Normal Septal', NULL, 'Anterior septal perforator arteries originate from the proximal part of the LAD and irrigate 2/3 of the upper part of the interventricular septum. They vary in number (usually from 4 to 13) with an average of eight branches. The length of these vessels ranges from 40 to 80 mm and tends to become shorter as they reach the apex. The first septal artery is usually the largest and the longest one (4 to 6 cm). Its external diameter at the origin ranges from 1.0 mm to 2.35 mm. Most commonly it originates close to the takeoff of the first diagonal branch. Less frequently the first septal perforator might be a short artery or there are two or three major septal arteries comparable in size . This pattern seems to protect the LAD against atherosclerosis. ', 15, '2019-08-11 06:43:13', '2019-08-11 07:53:44', NULL);
