INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (827, 'funs_cognition_level', '2', 'Mildly Impaired', 'Family and friends begin to notice difficulties with<br />getting the right word, remembering names, losing or<br />misplacing things, performing everyday tasks, planning<br />and organizing. A medical examination during this<br />Alzheimer''s stage may reveal problems with memory<br />and/or concentration.', NULL, 1204, '2016-08-25 11:50:55', '2022-05-04 15:53:03', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8884, 'pci_lesev_stent_thrombosis', 'definite', 'Definite Stent Thrombosis', NULL, '<div>Definite stent thrombosis is considered to have occurred by either angiographic or pathological confirmation<br /><br />Angiographic confirmation of stent thrombosis<br />&nbsp; &nbsp; Acute onset of ischemic symptoms at rest<br />&nbsp; &nbsp; New ischemic ECG changes that suggest acute ischemia<br />&nbsp; &nbsp; Typical rise and fall in cardiac biomarkers (refer to definition of spontaneous MI)<br /><br /><div>Nonocclusive thrombus</div><div>&nbsp;Intracoronary thrombus is defined as a (spheric, ovoid, or irregular) noncalcified</div><div>&nbsp;filling defect or lucency surrounded by contrast material (on 3 sides or within a</div><div>&nbsp;coronary stenosis) seen in multiple projections, or persistence of contrast material</div><div>&nbsp;within the lumen, or a visible embolization of intraluminal material downstream<br /><br />Pathological Confirmation of Stent Thrombosis<br /><br /><div>Evidence of recent thrombus within the stent determined at autopsy or via examination of</div><div>&nbsp;tissue retrieved following thrombectomy.</div></div></div><div>&nbsp;</div>', 1164, '2019-09-28 03:11:21', '2019-09-28 03:11:21', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8885, 'pci_lesev_stent_thrombosis', 'probable ', 'Probable Stent Thrombosis', NULL, '<div>Clinical definition of probable stent thrombosis is considered to have occurred after</div><div>intracoronary stenting in the following cases:</div><div>&nbsp;</div><div>a. Any unexplained death within the first 30 days5 940</div><div>&nbsp;</div><div>b. Irrespective of the time after the index procedure, any MI that is related to documented</div><div>acute ischemia in the territory of the implanted stent without angiographic confirmation</div><div>of stent thrombosis and in the absence of any other obvious cause</div>', 1164, '2019-09-28 03:13:42', '2019-09-28 03:13:42', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8889, 'pfup_stent_thrombosis_timing', 'late', 'Late', '&gt; 30 days &ndash; 1 year post stent implantation', '&gt; 30 days &ndash; 1 year post stent implantation', 237, '2019-09-28 03:29:23', '2019-09-28 03:29:23', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1344, 'res_term_infarction_type', '1', 'Type 1:  Spontaneous', '<p><strong>Spontaneous clinical syndrome</strong></p><ul><li>related to&nbsp;&nbsp;atherosclerotic plaque rupture, ulceration,&nbsp;&nbsp;fissuring, erosion, or dissection, with&nbsp;&nbsp; resulting intraluminal thrombus and&nbsp;leading to decreased myocardial blood&nbsp;&nbsp; flow or distal platelet emboli with ensuing&nbsp;&nbsp;myocyte necrosis.</li><li>This classification&nbsp;&nbsp;requires</li><li>a) detection of a rise and/or fall o&nbsp;&nbsp; cardiac biomarker values (preferably cTn)&nbsp;&nbsp; with at least 1 value &gt;99th percentile of&nbsp;the URL and b) at least 1 of the following:</li><li>&nbsp;Symptoms of myocardial ischemia</li><li>&nbsp;New or presumed new significant ST -&nbsp;T&nbsp;changes or new LBBB on the ECG</li><li>Development of pathological Q waves on&nbsp;the ECG</li><li>Imaging evidence of new loss of viable&nbsp;&nbsp; myocardium or new regional wall motion&nbsp;&nbsp;abnormality</li><li>Identification of an intracoronary thrombus&nbsp;by angiography or autopsy</li></ul><p>&nbsp;</p>', '<ul><li>cTn-I&nbsp;or T&nbsp;is the&nbsp;&nbsp;preferred biomarker.&nbsp;</li><li>&nbsp; If a cTn assay is not&nbsp;&nbsp; available, the best&nbsp;&nbsp; alternative is CKMB&nbsp;&nbsp;(measured by mass&nbsp;&nbsp; assay).</li><li>One or more&nbsp;&nbsp;coronary arteries&nbsp;&nbsp;may be involved.</li><li>The&nbsp;&nbsp; patient may have&nbsp;&nbsp;underlying severe&nbsp;&nbsp;coronary artery&nbsp;&nbsp;disease but on&nbsp;&nbsp;occasion may have&nbsp;&nbsp;nonobstructive or&nbsp;&nbsp;no coronary artery&nbsp;&nbsp;disease.</li></ul><p>&nbsp;</p>', 1604, '2016-08-25 11:50:55', '2019-10-16 02:51:30', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1483, 'mech_lv_support_other_tm', '2', 'Inserted during procedure and prior to PCI', '<ul>	<li>Mechanical ventricular support was&nbsp;inserted during the procedure, but &nbsp;prior&nbsp;to the PCI.</li></ul><p>&nbsp;</p>', NULL, 557, '2016-08-25 11:50:55', '2019-10-19 15:16:49', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1157, 'pci_lesev_dissection_type', 'b', 'Type B', '<div>&nbsp;</div><div>Grade B: Parallel tracts or double lumen separated by a radiolucent area during</div><div>contrast injection with no persistence after dye clearance</div>', NULL, 1187, '2016-08-25 11:50:55', '2019-09-28 03:41:06', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1067, 'pci_lesev_dissection_type', 'e', 'type E', '<p>&nbsp;Persistent filling defect with delayed antegrade flow</p>', NULL, 1187, '2016-08-25 11:50:55', '2019-09-28 03:38:03', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1155, 'pci_lesev_dissection_type', 'a', 'Type A', '<div>Grade A: Minor radiolucencies within the lumen during contrast injection with no</div><div>persistence after dye clearance</div>', NULL, 1187, '2016-08-25 11:50:55', '2019-09-28 03:40:03', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1134, 'pci_lesev_dissection_type', 'c', 'type C', '<div>Grade C: Extraluminal cap with persistence of contrast after dye clearance from the</div><div>700 lumen</div>', NULL, 1187, '2016-08-25 11:50:55', '2019-09-28 03:43:32', NULL);
