INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8903, 'covid_status', '1', 'COVID-19 Positive', 'The patient has tested positive for COVID-19.', NULL, 248, '2022-05-11 09:06:15', '2022-05-11 09:06:15', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8904, 'covid_status', '2', 'COVID-19 Suspected', 'Testing for COVID-19 was either not performed OR was negative; however, due to the presence of clinical signs/symptoms consistent with COVID-19 there was a high level of suspicion the infection was present which guided the overall treatment plan.', NULL, 248, '2022-05-11 09:09:52', '2022-05-11 09:09:52', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8968, 'covid_hospital_events', 'dvt_thrombosis', 'Deep Venous Thrombosis', 'Deep vein thrombosis (DVT) refers to the formation of one or more blood clots (a blood clot is also known as a &ldquo;thrombus,&rdquo; while multiple clots are called &ldquo;thrombi&rdquo;) in one of the body&rsquo;s large veins, most commonly in the lower limbs (e.g., lower leg or calf)', 'Office of the Surgeon General. (2008). The surgeon<br />general''s call to action to prevent deep vein thrombosis<br />and pulmonary embolism. <br /><br />Retrieved from<a href="https://www.ncbi.nlm.nih.gov/books/NBK44184/"> https://www.ncbi.nlm.nih.gov/books/NBK44184/</a>', 250, '2022-05-14 12:34:50', '2022-05-14 12:50:31', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8924, 'covid_status', '5', 'COVID-19 Testing Not Performed', 'The patient has not been tested for COVID-19.', NULL, 248, '2022-05-11 14:14:50', '2022-05-11 14:14:50', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8928, 'covid_rx', 'Azithromycin', 'Azithromycin', NULL, 'Azithromycin is a broad-spectrum macrolide antimicrobial and is among the most prescribed antimicrobial drugs in the United States. <br /><br />It is a derivative of erythromycin with greatly enhanced activity against gram-negative bacteria (including Enterobacteriaceae) and provides coverage of many gram-positive organisms.<br /><br />The role of azithromycin in the treatment of viral infections, including the respiratory syncytial virus and novel coronavirus SARS-CoV-2, is indeterminate. (October 17, 2021)<br /><br />', 249, '2022-05-11 14:40:36', '2022-05-11 14:40:36', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8929, 'covid_rx', 'Interferon', 'Interferon', NULL, 'Interferon therapy in patients with SARS, MERS, and COVID-19: A systematic review and meta-analysis of clinical studies&nbsp;<br /><br /><em> DOI: 10.1016/j.ejphar.2021.174248</em>', 249, '2022-05-11 14:45:25', '2022-05-11 14:45:25', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8933, 'covid_rx', 'Immunoglobulin IV', 'Intravenous Immunoglobulin', NULL, NULL, 249, '2022-05-11 15:08:32', '2022-05-11 15:08:32', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8934, 'covid_rx', 'Convalescent Plasma', 'Convalescent Plasma', NULL, '<p>The Omicron variant is the dominant SARS-CoV-2 variant currently circulating in the United States.<br /><br />Although in vitro data suggest that the CCP collected from vaccinated and unvaccinated individuals who have recovered from Omicron infection exhibits neutralizing activity against the Omicron variant</p><p>i<em>t is not possible toextrapolate the potential clinical efficacy of CCP in the current clinical context. </em><br /><em>This is due in part to the following factors:</em><br /><br />The current supply of CCP products in the United States was not generated from donors who had recovered from Omicron infection.<br /><br />Many CCP clinical trials were completed before the Omicron surge, and their results may not inform the current clinical context.<br /><br />The current approaches to testing CCP titers do not account for potential differences in the neutralizing activity of CCP products against currently circulating variants.<br /><br /><br /></p>', 249, '2022-05-11 15:14:15', '2022-05-11 15:14:15', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8935, 'covid_rx', 'Pirfenidone', 'Pirfenidone', NULL, '<strong>ATC&nbsp; L04AX05&nbsp;<br /></strong><br />Pirfenidone is approved for the treatment of Idiopathic Pulmonary Fibrosis (IPF) for patients with mild to moderate disease.<br /><br />Pirfenidone could inhibit apoptosis, downregulate ACE receptors expression, decrease inflammation by several mechanisms and ameliorate oxidative stress and hence protect pneumocytes and other cells from COVID-19 invasion and cytokine storm simultaneously. <br /><br />Based on the pirfenidone mechanism of action and the known pathophysiology of COVID-19, Pirfenidone has the potential for the treatment of COVID-19 patients.<br /><br /><br /><em>Med Hypotheses ; 144: 110005, 2020 Nov.</em><br /><br /><br /><br /><br />', 249, '2022-05-11 15:23:18', '2022-05-11 15:23:18', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8936, 'covid_rx', 'Eculizumab', 'Eculizumab', NULL, '<strong>Chemistry&nbsp;</strong><br />Eculizumab is a recombinant humanized monoclonal antibody against the complement protein C5.<br /><br />It is an immunoglobulin G-kappa (IgG&kappa;) consisting of human constant regions and murine complementarity-determining regions grafted onto human framework light and heavy chain variable regions.<br /><br />The compound contains two 448-amino acid heavy chains and two 214-amino acid light chains, and has a molecular weight of approximately 148 kilodaltons (kDa). (wikipedia)<br /><br />&nbsp;Eculizumab has the potential to be a key player in treatment of severe cases of COVID-19. Our results support eculizumab use as an off-label treatment of COVID-19, pending confirmation from the ongoing SOLID-C19 trial.<br /><br /><em>DOI: 10.26355/eurrev_202004_20875</em><br />', 249, '2022-05-11 16:16:54', '2022-05-11 16:16:54', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8942, 'covid_rx', 'Fingolimod', 'Fingolimod', NULL, 'Fingolimod, sold under the brand name Gilenya, is an immunomodulating medication, mostly used for treating multiple sclerosis', 249, '2022-05-11 18:17:34', '2022-05-11 18:17:34', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8944, 'covid_hospital_events', 'afib', 'Atrial Fibrillation', 'Atrial Fibrillation is a supraventricular tachyarrhythmia characterized by uncoordinated atrial activity with consequent deterioration of atrial mechanical function. On the electrocardiogram (ECG), atrial fibrillation is characterized by the replacement of consistent P waves with rapid oscillations or fibrillation waves that vary in amplitude, shape and timing, associated with an irregular, frequently rapid ventricular response when<br />atrioventricular conduction is intact.', NULL, 250, '2022-05-11 18:25:07', '2022-05-11 18:25:07', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8945, 'covid_hospital_events', 'no_invasive_ventilation', 'Noninvasive Positive Pressure Ventilation', 'Noninvasive positive pressure ventilation is the<br />administration of ventilatory support without utilizing an <br />artificial airway such as an endotracheal tube or<br />tracheostomy.', 'This includes any of the following devices with<br />ventilation delivered nasally, via mask or helmet:<br />1. Bilevel positive airway pressure (BiPAP)<br />2. Continuous positive airway pressure (CPAP)<br />3. Other non-invasive positive pressure devices', 250, '2022-05-11 18:31:05', '2022-05-11 18:31:05', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8930, 'covid_rx', 'Bevacizumab', 'Bevacizumab', NULL, '<strong>Avastin&nbsp;</strong><br />Vascular endothelial growth factor (VEGF) is known as the most potent inducing factors to increase vascular permeability. <br /><br />Bevacizumab is an anti VEGF recombinant humanized monoclonal antibody, which has been used in anti-tumor treatment for 16 years.<br /><br />Evidence suggest that Bevacizumab is a promising drug for severe and critical COVID-19 patients.', 249, '2022-05-11 15:02:34', '2022-05-11 15:02:34', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8932, 'covid_rx', 'Chloroquine', 'Chloroquine', NULL, 'Hydroxychloroquine does not reduce deaths from COVID-19, and probably does not reduce the number of people needing mechanical ventilation.<br /><br />Hydroxychloroquine caused more unwanted effects than a placebo treatment, though it did not appear to increase the number of serious unwanted effects.<br /><br />The authors do not think new studies of hydroxychloroquine should be started for treatment of COVID-19<br /><br /><em>doi.org/10.1002/14651858.CD013587.pub2</em><br />', 249, '2022-05-11 15:07:50', '2022-05-11 15:07:50', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8939, 'covid_rx', 'Sarilumab', 'Sarilumab', NULL, 'DOI: 10.1128/AAC.02107-21', 249, '2022-05-11 16:46:21', '2022-05-11 16:46:21', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8940, 'covid_rx', 'Fibrinolysis', 'Fibrinolysis', NULL, NULL, 249, '2022-05-11 16:49:05', '2022-05-11 16:49:05', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8941, 'covid_rx', 'Tocilizumab', 'Tocilizumab', NULL, 'Tocilizumab was granted an emergency use authorization (EUA) for the treatment of COVID-19 in the United States in June 2021', 249, '2022-05-11 18:11:52', '2022-05-11 18:11:52', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8915, 'covid_status', '3', 'COVID-19 Recovered', 'Check Private Notes', 'The patient was previously diagnosed with COVID-19 nfection (lab or clinical criteria) and is no longer contagious as defined by:<br /><br />T<strong>est-based strategy:</strong> <br />1. At least three (3) days (72 hours) have passed since recovery which is defined as <br />the<em> resolution of feve</em>r and improvement of respiratory symptoms, <br />2.&nbsp; AND<em> Two (2) consecutive negative COVID-19</em> laboratory tests &gt;= 24 hours apart<br /><br />OR&nbsp;<br /><br /><strong>Symptom-based strategy:&nbsp;</strong><br />At least three (3) days (72 hours) have passed since recovery which is defined as the resolution of fever and improvement of respiratory symptoms, <br />AND At least ten (10) days have passed since symptom<br />onset <br /><br />OR<br />If tested positive for COVID-19 and never exhibited symptoms:<br />At least 10 days have passed since first positive<br />COVID-19 test,<br /><br />OR<br /><br />Two (2) consecutive negative COVID-19 laboratory<br />tests &gt;= 24 hours apart<br />', 248, '2022-05-11 13:44:24', '2022-05-11 14:03:52', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8923, 'covid_status', '4', 'COVID-19 Negative', 'The patient has tested negative for COVID-19.', NULL, 248, '2022-05-11 14:14:02', '2022-05-11 14:14:02', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8925, 'covid_status', '99', 'Other', 'The patient has a COVID-19 status that is not available for selection.', NULL, 248, '2022-05-11 14:15:56', '2022-05-11 14:15:56', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8927, 'covid_rx', 'Hydroxychloroquine', 'Hydroxychloroquine', NULL, 'DOI: 10.1136/postgradmedj-2020-137785<br /><br />Hydroxychloroquine and chloroquine are medications that have been used for a long time. <br /><br />Their most common use is for the treatment and prophylaxis of malaria. However, these antimalarial drugs are known to also have anti-inflammatory and antiviral effects and are used for several chronic diseases such as systemic lupus erythematosus with low adverse effects. <br /><br />The antiviral action of hydroxychloroquine and chloroquine has been a point of interest to different researchers due to its mechanism of action. <br /><br />Several in vitro studies have proven their effectiveness on severe acute respiratory syndrome virus and currently both in vitro and in vivo studies have been conducted on 2019 novel coronavirus (covid-19).', 249, '2022-05-11 14:34:19', '2022-05-11 14:34:19', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8931, 'covid_rx', 'Corticosteroids IV', 'Intravenous Corticosteroids', NULL, NULL, 249, '2022-05-11 15:03:21', '2022-05-11 15:03:21', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8943, 'covid_rx', 'no_rx', 'No Treatment', 'Indicate if none of the listed COVID-19 therapies were administered during the episode of care.', NULL, 249, '2022-05-11 18:19:53', '2022-05-11 18:19:53', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8937, 'covid_rx', 'Remdesivir', 'Remdesivir', NULL, 'Remdesivir is an antiviral medicine with properties to inhibit viral replication of SARS-CoV-2.<br /><br />Authors'' conclusions:<br />Based on the currently available evidence, we are moderately certain that remdesivir probably has little or no effect on all-cause mortality at up to day 28 in hospitalised adults with SARS-CoV-2 infection. <br /><br /><strong>We are uncertain about the effects of remdesivir on clinical improvement and worsening. </strong><br /><br />There were insufficient data available to validly examine the effect of remdesivir on mortality in subgroups depending on the extent of respiratory support at baseline. <br /><br />Future studies should provide additional data on efficacy and safety of remdesivir for defined core outcomes in COVID-19 research, especially for different population subgroups. <br /><br />This could allow us to draw more reliable conclusions on the potential benefits and harms of remdesivir in future updates of this review. Due to the living approach of this work, we will update the review periodically.<br /><br /><em>DOI: 10.1002/14651858.CD014962</em><br /><br />', 249, '2022-05-11 16:22:15', '2022-05-11 16:22:15', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8938, 'covid_rx', 'Famotidine', 'Famotidine', NULL, 'Famotidine is a competitive histamine H2-receptor antagonist most commonly used for gastric acid suppression but thought to have potential efficacy in treating patients with Coronavirus disease 2019 (COVID-19).<br /><br />We found no evidence of a reduced risk of COVID-19 outcomes among hospitalized COVID-19 patients who used famotidine compared with those who did not or compared with PPI or hydroxychloroquine users.<br /><br /><em>DOI: 10.14309/ajg.0000000000001153&nbsp;</em><br /><br />', 249, '2022-05-11 16:43:08', '2022-05-11 16:43:08', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8946, 'covid_hospital_events', 'vfib', 'Ventricular Fibrillation', 'Fibrillation is an uncontrolled twitching or quivering of muscle fibers occurring in the lower chambers of the heart (ventricles).', NULL, 250, '2022-05-12 15:01:39', '2022-05-12 15:01:39', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8947, 'covid_hospital_events', 'vv_ecmo', 'Venovenous Extracorporeal Membrane Oxygenation', 'Venovenous extracorporeal membrane oxygenation (VV ECMO) is a system that drains blood from a large central vein and pumps it through a gas-exchange device that oxygenates the blood while removing carbon dioxide before returning it through a large<br />central vein.', 'ECMO utilized to provide ventricular support is not captured here (e.g. VA ECMO)', 250, '2022-05-12 15:05:14', '2022-05-12 15:11:36', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8948, 'covid_hospital_events', 'vtachy', 'Ventricular Tachycardia', 'Ventricular tachycardia (VT) that is &gt; 30 seconds in duration and/or requires termination due to hemodynamic compromise in &lt; 30 seconds.', NULL, 250, '2022-05-12 15:13:34', '2022-05-12 15:13:34', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8949, 'covid_hospital_events', 'renal_replacement', 'Renal Replacement Therapy', 'Renal replacement therapy includes any of the following:<br />1. Hemodialysis<br />2. Peritoneal Dialysis<br />3. Continuous Venovenous Hemofiltration<br />4. Continuous Venovenous Hemodialysis', NULL, 250, '2022-05-12 15:16:56', '2022-05-12 15:16:56', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8950, 'covid_hospital_events', 'heart_failure', 'Heart Failure', 'Heart failure is a complex clinical syndrome that results 20 from any structural or functional impairment of H ventricular filling or ejection of blood. <br />The cardinal e2 manifestations of HF are dyspnea and fatigue, which may limit exercise tolerance, and fluid retention, which may lead to pulmonary and/or splanchnic congestion<br />and/or peripheral edema. <br />Some patients have exercise intolerance but little evidence of fluid retention, whereas others complain primarily of edema, dyspnea, or fatigue. <br />Because some patients present without signs or symptoms of volume overload, the term "heart failure" is preferred over "congestive heart failure." <br />There is no single diagnostic test for HF because it is largely a clinical diagnosis based on a careful history and physical examination.', NULL, 250, '2022-05-12 15:22:40', '2022-05-12 15:22:40', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8951, 'covid_hospital_events', 'stroke_embolic', 'Stroke - Embolic', 'An embolic stroke is an acute episode of focal or global neurological dysfunction caused by a blood clot which formed elsewhere in the body and traveled in the bloodstream to the brain.', NULL, 250, '2022-05-12 15:36:50', '2022-05-12 15:36:50', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8952, 'covid_hospital_events', 'stroke_hemorrhgic', '&nbsp;Stroke Hemorragic', 'Hemorrhagic stroke is defined as an acute episode of focal or global cerebral or spinal dysfunction caused by intraparenchymal, intraventricular, or subarachnoid<br />hemorrhage.', 'Hemorrhage may be a consequence of ischemic stroke.<br />In this situation, the stroke is an ischemic strok with hemorrhagic transformation and not a hemorrhagic stroke.<br /><br /><strong>Note:</strong> Subdural hematomas are intracranial hemorrhagic<br />events and not strokes.<br /><br />', 250, '2022-05-14 08:50:29', '2022-05-14 08:50:29', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8953, 'covid_hospital_events', 'myocarditis', 'Myocarditis', 'Myocarditis is an inflammatory disease of the myocardium resulting from viral infections and/or post- viral immune-mediated responses.', 'Kindermann I, Barth C, Mahfoud F, et al. Update on Myocarditis. J Am Coll Cardiol 2012;59:779-792.', 250, '2022-05-14 09:05:00', '2022-05-14 09:05:00', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8954, 'covid_hospital_events', 'pericarditis', 'Pericarditis', 'Pericarditis is the inflammation of the pericardial layers characterized by chest pain, electrocardiographic&nbsp; changes and often pericardial effusion. It is often the result of an infectious or a noninfectious process but can also be idiopathic.', 'Chiabrando JG, Bonaventure A, Vecchie A, et al.<br />Management of acute and recurrent pericarditis. J Am<br />Coll Cardiol 2020;75:76-92.', 250, '2022-05-14 09:09:49', '2022-05-14 09:09:49', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8955, 'covid_hospital_events', 'Inotropes_iv', 'Inotrops Intravenous', 'Inotropic agents increase myocardial contractility.', 'Inotropic agents increase myocardial contractility. Any<br />of the following medications are considered inotropes:<br /><ul><li>Amrinone&nbsp;</li><li>Dobutamine</li><li>Inamrinone</li><li>Milrinone</li></ul>', 250, '2022-05-14 09:16:11', '2022-05-14 09:26:55', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8956, 'covid_hospital_events', 'stroke _undetermined', 'Stroke - Undetermined', 'A stroke of undetermined origin is defined as an acute episode of focal or globa eurological dysfunction caused by presumed brain, spinal cord, or retinal vascular injury as a result of hemorrhage or infarction but <em>with insufficient information to allow categorization&nbsp; as chemic or hemorrhagic.</em>', NULL, 250, '2022-05-14 09:29:53', '2022-05-14 09:29:53', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8957, 'covid_hospital_events', ' vasopressors_iv', 'Intravenous Vasopressor(s)', 'Vasopressor agents cause vasoconstriction. Any of<br />the following medications are considered<br />vasopressors: Dopamine Epinephrine Norepinephrine Phenylephrine Vasopressin', NULL, 250, '2022-05-14 09:34:54', '2022-05-14 09:34:54', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8970, 'covid_hospital_events', 'lv_support', 'mechanical Support', 'Mechanical Ventricular<br />Support', NULL, 250, '2022-05-14 13:27:59', '2022-05-14 13:28:49', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8958, 'covid_hospital_events', 'intravascular_coag', 'Disseminated Intravascular Coagulation', 'A syndrome caused by an underlying disease process which leads to the activation of coagulation resulting in bleeding/hemorrhage and intravascular thrombosis.', '&nbsp;DIC is a systemic pathophysiologic process and not a single disease entity, resulting from an overwhelming activation of coagulation that consumes platelets and coagulation factors and causes microvascular fibrin thrombi, which can result in multiorgan dysfunction syndrome from tissue ischemia. Some conditions associated with acute DIC include septic shock, exsanguinating trauma, burns, or acute promyelocytic leukemia.<br /><br />&nbsp;The massive tissue factor stimulus results in excess intravascular thrombin, which overcomes the anticoagulant systems and leads to thrombosis. Because of consumption of coagulation factors and platelets, DIC also has a hemorrhagic phase. Treatment of the bleeding patient with DIC is supportive with the use of blood components.<br /><br /><em>DOI: 10.1093/ajcp/aqw195<br /></em><br />The cornerstone of supportive treatment of this coagulopathy is management of the underlying condition. <br />Additionally, administration o<strong>f heparin</strong> may be useful, and restoration of physiological anticoagulants has been suggested, but has not been proven successful in improving clinically relevant outcomes so far.<br />In patients with major bleeding or at risk for hemorrhagic complications, <strong>administration of platelet concentrates, plasma, or coagulation factor concentrates should be considered.</strong>', 250, '2022-05-14 09:45:22', '2022-05-14 09:45:22', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (8966, 'covid_hospital_events', 'dvt_thrombosis', 'Deep Venous Thrombosis', 'Deep vein thrombosis (DVT) refers to the formation of one or more blood clots (a blood clot is also known as a &ldquo;thrombus,&rdquo; while multiple clots are called &ldquo;thrombi&rdquo;) in one of the body&rsquo;s large veins, most commonly in the lower limbs (e.g., lower leg or calf)', 'Office of the Surgeon General. (2008). The surgeon<br />general''s call to action to prevent deep vein thrombosis<br />and pulmonary embolism. <br /><br />Retrieved from htps://www.ncbi.nlm.nih.gov/books/NBK44184/', 250, '2022-05-14 12:31:24', '2022-05-14 12:31:24', NULL);
