INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1174, 'uni_death_cause', '1', 'Cardiac', '<ul><li><strong>Cardiovascular death</strong></li><li>Indicates cause of death was sudden cardiac death</li><li>View Private Notes for details</li></ul>', 'Indicates cause of death was sudden cardiac death, MI, unstable angina, or other CAD; vascular death (e.g. stroke, arterial embolism, pulmonary embolism, ruptured aortic aneurysm, or dissection); CHF: or cardiac arrhythmia, consider further specification such as - MI - Ischaemic stroke - Primary arrhythmic death (without MI) - Progressive heart failure - Haemorrhage- related death - Unexplained sudden death', 127, '2016-08-25 11:50:55', '2022-05-19 18:14:56', NULL);
