INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (32, 'cath_mv_stenosis_stage', 'stage_b', 'Stage B: Progressive MS', '<p><strong><span style="background-color: #ffff00;">Progressive MS&nbsp;</span></strong></p><p><strong><span style="background-color: #00ff00;">Valve Anatomy&nbsp;</span>&nbsp;</strong></p><ul><li>&nbsp;Rheumatic valve changes with&nbsp;&nbsp;commissural fusion and&nbsp;&nbsp; diastolic doming of the mitral&nbsp;&nbsp; valve leaflets&nbsp;</li><li>Planimetered MVA &gt;1.5 cm2</li></ul><p><strong><span style="background-color: #00ff00;">Valve Hemodynamics&nbsp;</span>&nbsp;</strong></p><ul><li>&nbsp;Increased transmitral flow&nbsp;&nbsp;velocities&nbsp;</li><li>MVA &gt;1.5 cm&nbsp;</li><li>Diastolic pressure half-time&nbsp;&nbsp; &lt;150 ms</li></ul><p><strong><span style="background-color: #00ff00;">Hemodynamic Consequences &nbsp;</span>&nbsp;</strong></p><ul><li>Mild-to-moderate LA&nbsp;&nbsp;enlargement&nbsp;</li><li>Normal pulmonary pressure&nbsp;&nbsp;at rest</li></ul><p><strong><span style="background-color: #00ff00;">&nbsp;Symptoms &nbsp;</span></strong></p><ul><li>Noneâ</li></ul>', NULL, 2331, '2016-08-25 11:50:55', '2022-05-29 13:19:05', NULL);
