INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (9178, 'chest_pain_ecg', 'normal', 'Nonischemic or normal', 'Nonspecific ST-T wave abnormalities, which may indicate nonischemic changes, are usually defined as ST deviation of &lt;0.5 mm (0.05 mV) or T-wave inversion of &lt;2 mm (0.2 mV); ECG showing normal findings.', NULL, 4097, '2022-11-06 08:44:17', '2022-11-06 08:50:15', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (1133, 'cath_cad_presentation_type', '6', 'ST-Elevation MI (STEMI) orequivalent', 'The patient presented with a ST elevation myocardial infarction (STEMI) or its equivalent as documented in the medical record.', '<p>The patient presented with a ST elevation myocardial infarction (STEMI) or its equivalent as documented in the medical record.</p><p>STEMIs are characterized by the presence of both criteria:</p><ul><li>a. ECG evidence of STEMI:</li><li>New or presumed new ST-segment elevation or new left bundle branch block not documented to be resolved within 20 minutes.</li><li>ST-segment elevation is defined by new or presumed new sustained ST-segment elevation at the J-point in two contiguous electrocardiogram (ECG) leads with the cut-off points: &gt;=0.2 mV in men or &gt;= 0.15mV in women in leads V2-V3 and/or &gt;= 0.1 mV in other leads and lasting greater than or equal to 20 minutes.</li><li>If no exact ST-elevation measurement is recorded in the medical chart, physician''s written documentation of ST-elevation or Q-waves is acceptable. If only one ECG is performed, then the assumption that the ST elevation persisted at least the required 20 minutes is acceptable.</li><li>Left bundle branch block (LBBB) refers to new or presumed new LBBB on the initial ECG.</li><li>b. Cardiac biomarkers (creatinine kinase-myocardial band, Troponin T or I) exceed the upper limit of normal according to the individual hospital''s laboratory parameters a clinical presentation which is consistent or suggestive of ischemia which is consistent or suggestive of ischemia. Note:</li><li>For purposes of the Registry, ST elevation in the posterior chest leads (V7 through V9), or ST depression that is maximal in V1-3, without ST-segment elevation in other leads, demonstrating posterobasal myocardial infarction, is considered a STEMI equivalent and qualifies the patient for reperfusion therapy.</li></ul>', 603, '2016-08-25 11:50:55', '2022-06-05 16:38:09', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (803, 'cath_cad_presentation_type', '5', 'Non-STEMI', '<p>The patient was hospitalized for a non-ST elevation myocardial infarction (STEMI) <br />As documented in the medical record.</p>', '<ul><li>Non-STEMIs are characterized by the presence of both criteria:</li><li>a. Cardiac biomarkers (creatinine kinase-myocardial band, Troponin T or I) exceed the upper limit of normal according to the individual hospital''s laboratory parameters with a clinical presentation which is consistent or suggestive of ischemia. ECG changes and/or ischemic symptoms may or may not be present.</li><li>b. Absence of ECG changes diagnostic of a STEMI (see STEMI).</li></ul>', 603, '2016-08-25 11:50:55', '2022-06-05 16:39:45', NULL);
INSERT INTO public.defs_options (id, name, code, selection_text, selection_definition, selection_notes, definition_id, inserted_at, updated_at, selection_reference) VALUES (796, 'cath_cad_presentation_type', '4', 'Unstable angina', '<p><strong>There are three principal presentations of unstable angina:</strong>&nbsp;</p><ul><li>1. Rest angina (occurring at rest and prolonged, usually &gt;20 minutes);</li><li>2. New- onset angina (within the past 2 months, of at least Canadian Cardiovascular Society Class III severity); or</li><li>3. Increasing angina (previously diagnosed angina that has become distinctly more frequent, longer in duration, or increased by 1 or more Canadian Cardiovascular Society class to at least CCS III severity).</li></ul>', NULL, 603, '2016-08-25 11:50:55', '2022-06-05 18:06:54', NULL);
